var sFormId       = this.GetForm().id;
var sBUTTONLIST   = 'TTTTTTT';                          //공통기능정의(버튼순으로 T or F)
var sUSERAUTH     = '';                                 //사용자별 프로그램 사용 권한
var sFORMCAPTION  = '';                                 //Form의 Title 정보
var sFORMLOCATION = '';                                 //Form의 Location 정보
var sUSERLAVEL    = '';                                 //Form의 사용  Level
var vREVOBJ       = Array(9);                           //결과값 처리를 위한 오브젝트 
var sGRDTIT       = "";                                 //그리드 제목
var sCUROBJ       ;                                     //현재 LOST FOCUS 오브젝트

var sMenuGrpNum     = "0";                              // 메뉴그룹의 갯수보관
var sMenuListNum    = "0";                              // 메뉴리스트의 갯수보관
var sMenuUpId       = "";
var sMenuUpNm       = "";

var gnGrpRow        = 0;
/***************************************************************************************************
*                                         공통 Event 처리 부분                                     *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'TTFFTTT';     // T초기화,T조회,F저장,F삭제,T엑셀,F인쇄,T닫기

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

    //공통코드 조회
	fcd_GetComboDs(dsInComCd);

	// 기준년도 값 설정
	fcd_SetStndYear(dsStndYear, dsCond.GetColumn(0,"MIN_STND_YEAR"), substr(fcd_GetSysdate("YYYYMMDD"), 0, 4)); 
	cboStndYear.Value = dsCond.GetColumn(0,"STND_YEAR");

	// 기준월 값 설정
	cboStndMonth.Value = fcd_GetSysdate("MM");

	fcSearch();
}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "";  //"dsOrganExamBrncCycle|dsOrganExamBrncBoat";
	if (   !fnc_FormUnloadCheck(sDataset  , sFormId) ) return false;
}

/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
	dsVeriProcStat.ClearData();
    dsVeriSumm.ClearData();
    dsVeriDetl.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
	// 화면 종료
	Close();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 체크 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    //조회조건 체크및 trim처리
    fcInit(obj);    //기존 데이터셋 초기화
    return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

    var sServiceName = "rbr5020-service:searchVeriProcStat";
    var sInDataSet   = "";
    var sOutDataSet  = "dsVeriProcStat=dsVeriProcStat dsVeriSumm=dsVeriSumm dsVeriDetl=dsVeriDetl";

    fcd_AddParam("STND_YEAR", cboStndYear.Value);				// 기준년도
	fcd_AddParam("STND_MM", cboStndMonth.Value);				// 기준월
	fcd_AddParam("USER_ID", gdsUser.getColumn(0,"USER_ID"));	// 사번
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

/*-------------------------------------------------------------------------------------------------+
>>  확정 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcConfirmCheck(obj) {
	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  확정 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcConfirm(obj) {

	if ( !fcConfirmCheck() ) return;

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	sServiceName = "rbr5020-service:saveVeriStat";
	sInDataSet   = "dsVeriProcStat=dsVeriProcStat ";
	sOutDataSet  = "";

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  취소 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcCancelCheck(obj) {
	var chkcnt = 0;
	for ( var i = 0; i < dsVeriSumm.TotalRowCount(); i++ ) {
		if(dsVeriSumm.GetColumn(i, "CHK") == '1') chkcnt++;
	}
	if(chkcnt == 0) return false;
	else return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcCancel(obj) {

	if ( !fcCancelCheck() ) return;

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	sServiceName = "rbr5020-service:saveCancelStat";
	sInDataSet   = "dsVeriSumm=dsVeriSumm ";
	sOutDataSet  = "";

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


/***************************************************************************************************
*                          CALL BACK 처리 부분                                                     *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {


	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if ( sServiceName == "rbr5020-service:searchVeriProcStat" ) {
		//검수상태 설정
		btnCfmVeri.Enable = dsVeriProcStat.getColumn(0,"CFM_ENABLE");	//확정버튼 활성화 여부
		btnCfmVeri.Text = dsVeriProcStat.getColumn(0,"CFM_STAT");		//확정상태
		stMeetCd = dsVeriProcStat.getColumn(0,"MEET_CD");		//경륜경정구분코드
		stMeetNm = dsVeriProcStat.getColumn(0,"MEET_NM");		//경륜경정명
		stMessage.Text = dsVeriProcStat.getColumn(0,"CFM_MESSAGE");

		btnCncl.Enable = dsVeriProcStat.getColumn(0,"CNCL_ENABLE");	//지점취소 버튼 활성화 여부
	}	

	// 확정 완료 후
	if ( sServiceName == "rbr5020-service:saveVeriStat" ) {
		fcSearch();
	}			

	// 취소 완료 후
	if ( sServiceName == "rbr5020-service:saveCancelStat" ) {
		fcSearch();
	}

}

/*-------------------------------------------------------------------------------------------------+
>>  그리드 정렬
+-------------------------------------------------------------------------------------------------*/
function fcGridSort(obj,nCell,nX,nY) {
	// 특정 Head 클릭 시 해당 항목으로 Sort(만약 특정 컬럼을 누를때만 소트하고자 한다면 nCell로 조건)
	if ( nCell == 0 ) {
 		fnc_GridAllChk(obj);
	} else {
		fnc_GridSort(obj, object(obj.BindDataset), nCell);
	}
}

function fcOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcSearch(obj);
}

/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {
	var sExcelList = "grdVeriDetl:검수결과세부";
    fcd_OpenExcelListExport(sExcelList,1);
}

function fcBtnOnClick(obj)
{
	switch( obj.id ) {
		case "btnCfmVeri" :
			fcConfirm(obj);
			break;
		case "btnCncl" :
			fcCancel(obj);
			break;	
	}

}

/*-------------------------------------------------------------------------------------------------+
>>  프린트 인쇄 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcPrint(obj) {

	//데이터셋을 초기화 한다.
	ds_reportParam.DeleteAll();
	var nRnum;

    // 필드 값을 셋팅한다.
    var paramField = [
							"STND_YEAR"	
						,	"USER_ID"	
						,	"STND_MM"	
					 ];

	// 값을 셋팅한다.
	var paramValue = [
						  cboStndYear.Value
						, gdsUser.getColumn(0,"USER_ID")
						, cboStndMonth.Value
					 ];

	var rowCnt = paramField.length;	
	for(var row = 0 ; row < rowCnt; row++)
	{
		nRnum = ds_reportParam.AppendRow();
		ds_reportParam.SetColumn(nRnum, "PARAM_NM", paramField[row]);
		ds_reportParam.SetColumn(nRnum, "PARAM_VAL",paramValue[row]);
	}

	/* 레포트 배율 추가 - 시작 */
	iRow = ds_reportParam.AddRow();

	// Default Scale 파라미터명 추가(고정)
	ds_reportParam.SetColumn(iRow, "PARAM_NM", "DEFAULT_SCALE");
	ds_reportParam.SetColumn(iRow, "PARAM_VAL", "1");

	//레포트 트랙잭션을 태운다.
	fnc_ReportPreView("rbr5020", ds_reportParam.ID, "N");

}
