/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'FT'+sInpt+'FTFT';     //초기화,조회,저장,삭제,엑셀,인쇄,닫기
    //sBUTTONLIST   = 'TTTTTTT'; 

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);


    //공통코드 조회
	fcd_GetComboDs(dsInComCd);


	/*
		Div 가 Loading 시간이 걸린다. 
		반드시 timer 로 처리해야함.
	*/

	// 부서목록
	fcGetDprt();

	// 년도 설정
	fcSetYear();


	// 로그인한 사용자 부서가 보여지게 한다.

	// 20111028_장한너울 (사용안함) -- dsOutTeamNm 조회로 교체(combo 사용)
	//edDprtCd.value   = gdsUser.getColumn(0, "DEPT_CD");  // 부서코드

	cboTeamNm.Value  = gdsUser.getColumn(0, "TEAM_CD");  // 부서코드	
	edDprtName.value = gdsUser.getColumn(0, "TEAM_NM");  // 부서명

	// Combo 에 전체 추가 : 회계구분, 차수구분
	dsAccount.InsertRow(0);		
	dsAccount.SetColumn(0,"CD"   , "" );
	dsAccount.SetColumn(0,"CD_NM", "전체");

	cboAccount.Index = 0;


	// 화면 열리자마자 조회
	fcSearch();

}

