/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	if (!fcSearchCheck(obj)) return;// 필수조건 체크 및 그리드 초기화

	var sServiceName = "rbs1020-service:searchList";  // Grid 조회
	var sInDataSet   = "";
	var sOutDataSet  = "dsList=dsList ";     

	fcd_AddParam("BUSI_YEAR"  , cboYear.Value);      // 년도  	
	fcd_AddParam("ACC_BGN_CD" , cboAccount.Value);   // 회계구분
	fcd_AddParam("DPRT_CD"	  ,  cboTeamNm.Value);	// 부서코드(대안)

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);	
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {

	// 승인금액과 집행금액 비교확인
	var nRow = dsList.row;                             
	var nConfirmAmt = toNumber(dsList.GetColumn(nRow, "CONFIRM_AMT"));
	var nQuar1 		= toNumber(dsList.GetColumn(nRow, "QUAR_1"));
	var nQuar2 		= toNumber(dsList.GetColumn(nRow, "QUAR_2"));
	var nQuar3 		= toNumber(dsList.GetColumn(nRow, "QUAR_3"));
	var nQuar4 		= toNumber(dsList.GetColumn(nRow, "QUAR_4"));
	var nQuar 		= nQuar1 + nQuar2 + nQuar3+ nQuar4;

	if (lower(dsList.GetRowType(nRow)) != "insert") {
		if( nConfirmAmt != nQuar){
			fnc_Message(SNIS_CRA_B999, "N","집행금액을 다시 확인하시기바랍니다!\n\n(승인금액과 일치하지 않습니다)");
			mskQuar4.SetFocus();
			return;
		}
	}

	// 저장할때 자산 + 시설 = 1 + 2+ 3+ 4  이 아니면 메시지 보여주기
	if ( !fcCheckQuarSum() ) return; 


    // 입력상태코드가 001 이면 입력마감상태 이므로 수정하고 저장 못한다.
    var sTemp = dsList.GetColumn(dsList.row, "INPT_END_CD");
    if(sTemp == '001')
    {
		fnc_Message(SNIS_RBM_E003);  // 입력마감상태이므로 수정할 수 없습니다.
		return;
	}	


	//if ( !fcSaveCheck() ) return;  첨부파일만 올려서 저장할수 있으므로  주석처리한다.


	var sServiceName = "rbs1020-service:saveBusiPlanDetlReg";
	var sInDataSet   = "dsList=dsList:u dsUploadFile=dsUploadFile:u";
	var sOutDataSet  = "dsList=dsList ";

	//fcd_AddParam(Trim("DEL_YN"), "N");
	//fcd_AddParam("GRP_NM", edSearchTxt.text);
	//첨부파일 서버 업로드 

	if( dsUploadFile.GetRowCount() > 0 ) {
		for ( i = 0; i < dsUploadFile.GetRowCount(); i++ ) {
			if(dsUploadFile.getColumn(i, "CHK") == "1" ) {
				var rtn_arr = fnc_FileWrite(  divBtnLst.HttpFile 
											, dsUploadFile.GetColumn(i, "FILE_PATH" )
											, dsUploadFile.GetColumn(i, "FILE_NM" ) 
											,"");

				var fileSize = divBtnLst.HttpFile.GetFileSizeLocal(dsUploadFile.GetColumn(i, "FILE_PATH" ));

				if ( rtn_arr[0] != "SUCC" ) {
					alert(rtn_arr[1] + "파일 업로드 에러발생");
					return false;
				}

				//dsUploadFile.SetColumn(i, "SEQ_NO"      , dsOutNextSeqNo.getColumn(0,"NEXTSEQNO") );
				//dsUploadFile.SetColumn(i, "FILE_SEQ"    , toInteger(dsOutNextSeqNo.getColumn(0,"NEXTFILESEQNO")) + toInteger(i) );
				dsUploadFile.SetColumn(i, "FILE_PATH"    , rtn_arr[1] );
				dsUploadFile.SetColumn(i, "FILE_SIZE"   , fileSize );

			} // end if
		}
	} // end 



	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

