/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {

	if (obj.GetForm().id == "specBtnRow") {
		if (cboAccount.Value != "001" && cboAccount.Value != "002") {
			fnc_message("회계단위를 선택후 입력이 가능합니다.", "N");
			return;
		}
		var nRow = dsList.AddRow();
		dsList.SetColumn(nRow, "MNG_NAME", gdsUser.GetColumn(0, "USER_NM"));
		dsList.SetColumn(nRow, "MNG_ID", gdsUser.GetColumn(0, "USER_ID"));
		dsList.SetColumn(nRow, "DPRT_CD", gdsUser.GetColumn(0, "TEAM_CD"));
		dsList.SetColumn(nRow, "DPRT_NAME", gdsUser.GetColumn(0, "TEAM_NM"));
		dsList.SetColumn(nRow, "BUSI_YEAR", substr(today(),0,4));
		dsList.SetColumn(nRow, "CONFIRM_AMT", 0);

		dsList.SetColumn(nRow, "ACC_BGN_CD", cboAccount.Value);
		dsList.SetColumn(nRow, "ACC_BGN_NM", cboAccount.Text);

		fcSetEnable("",gdsUser.getColumn(0, "TEAM_CD"));
		edBusiNm.Readonly = false;
		edBusiNm.SetFocus();

	} else if(obj.GetForm().id == "fileBtnRow") {
		sResponse = fcd_GetSelectFile();	
		row = dsUploadFile.addRow();

		dsUploadFile.SetColumn(row, "ATT_FILE_KEY" , dsList.GetColumn(dsList.currow,"ATT_FILE_KEY"));

		if ( !fnc_IsNull(sResponse) ) {
			dsUploadFile.SetColumn(row, "CHK"       , "1"         );
			dsUploadFile.SetColumn(row, "FILE_PATH"  , sResponse[0] + "\\" + sResponse[1]);
			dsUploadFile.SetColumn(row, "FILE_NM" , sResponse[1]);
		}	

		if ( dsUploadFile.getColumn(row, "CHK") != "1") {
			dsUploadFile.rowcount = dsUploadFile.DeleteRow(row);
		}
	}

}


