/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
    if (obj.GetForm().id == "specBtnRow") {
		for ( var i = 0; i < dsList.rowcount; i++ ) {
			if(dsList.getColumn(i, "CHK") == 1) {
				dsList.deleteRow(i);
			}
		}
	} else if (obj.GetForm().id == "fileBtnRow") {
		var splitArr    = (dsUploadFile.getColumn(dsUploadFile.currow, "FILE_PATH")).split("\\");
		var lenSplitArr = toInteger(length(splitArr)) - toInteger(1);

		//파일삭제
		for ( var i = 0; i < dsUploadFile.rowcount; i++ ) {
			if(dsUploadFile.getColumn(i, "CHK") == 1) {
				dsUploadFile.deleteRow(i);
			}
		}

		fnc_FileDelete(splitArr[lenSplitArr]);
            //fcFileDelete();
    }
}


/***************************************************************************************************
*                          CALL BACK 처리 부분                                                     *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {


	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if ( sServiceName == "rbs1020-service:searchList" ) {
		if(dsList.rowcount > 0){
			dsList.row = -1;
			dsList.row =  0;
			edBusiNm.Readonly = true;
		}
		dsUploadFile.ClearData();	
	}		

	// 저장 완료 후
	if ( sServiceName == "rbs1020-service:saveBusiPlanDetlReg" ) {
		fcSearch();  // 다시 조회한다.
	}			


}


