/*-------------------------------------------------------------------------------------------------+
>>  첨부파일 조회 
+-------------------------------------------------------------------------------------------------*/
function fcSearchFile(sAttFileKey){

		var sServiceName = "";
		var sInDataSet   = "";
		var sOutDataSet  = "";


		 sServiceName = "rbs1020-service:searchFileMana";
		 sInDataSet   = "";
		 sOutDataSet  = "dsUploadFile=dsUploadFile ";

		 //조회조건 : 
		 fcd_AddParam("ATT_FILE_KEY", sAttFileKey);

		 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}



/*-------------------------------------------------------------------------------------------------+
>>  분기 금액을 입력할때마다 합계 항목을 갱신한다.
+-------------------------------------------------------------------------------------------------*/
function mskQuar_OnCharChanged(obj,strPreText,strPostText)
{
	var nQuar1 = toFloat(mskQuar1.Value);  // 계획 1/4 분기
	var nQuar2 = toFloat(mskQuar2.Value);  // 계획 2/4 분기
	var nQuar3 = toFloat(mskQuar3.Value);  // 계획 3/4 분기
	var nQuar4 = toFloat(mskQuar4.Value);  // 계획 4/4 분기

	var nQuarSum = nQuar1 + nQuar2 + nQuar3 + nQuar4;   // 분기 합

	mskQuarSum.value = nQuarSum;   // 분기 합 셋팅

}


/*-------------------------------------------------------------------------------------------------+
>>  저장할때 자산취득금액 + 시설관리금액 = 1 + 2+ 3+ 4  이 아니면 메시지 보여주기
+-------------------------------------------------------------------------------------------------*/
function fcCheckQuarSum()
{
    // 1 분기 +  2 분기 + 3 분기 + 4 분기
	var nQuar1 = toFloat(mskQuar1.Value);  // 계획 1/4 분기
	var nQuar2 = toFloat(mskQuar2.Value);  // 계획 2/4 분기
	var nQuar3 = toFloat(mskQuar3.Value);  // 계획 3/4 분기
	var nQuar4 = toFloat(mskQuar4.Value);  // 계획 4/4 분기

	var nQuarSum = nQuar1 + nQuar2 + nQuar3 + nQuar4;   // 분기 합


	// 자산취득금액 + 시설관리금액
	var nAssProAmt = toFloat(mskAssProAmt.Value);     // 자산취득금액
	var nFacManaAmt = toFloat(mskFacManaAmt.Value);   // 시설관리금액

	var nAFSum = nAssProAmt + nFacManaAmt;


	// 메시지처리 
	if(nQuarSum != nAFSum)
	{
	    // 자산취득비 + 시설관리유지비 = 1분기 + 2분기+ 3분기+ 4분기 이어야 합니다.  
	    fnc_Message(SNIS_RBM_E004); 

	    return false;
	}    

	return true;
}



/*-------------------------------------------------------------------------------------------------+
>>  입력상태코드가 001 이면 입력마감상태 항목들의 Enable = false 로 한다.
>>  
>>                입력상태코드( 54 ) 
>>                001 - 입력마감 
>>                002 - 마감해지
>>  
+-------------------------------------------------------------------------------------------------*/
function fcSetEnable(strCode,strDprtCd)
{

	var strDeptCd = gdsUser.getColumn(0, "TEAM_CD");  	// 부서코드	
tRACE(strCode+":"+strDeptCd+":"+strDprtCd);
	if( strCode != "001" && strDeptCd == strDprtCd ){
		btnDprtPop2.Enable 		= true;   // 상세화면 부서팝업 버튼		
		//cboAccountDetl.Enable 	= true;   // 회계구분 Combo		
		edBusiNm.Enable 		= true;   // 사업명		
		taBusiPrps.Enable 		= true;   // 추진배경 및 목적
		taBusiSmry1.Enable 		= true;   // 개요		
		calBusiStr.Enable 		= true;   // 사업 시작년월
		calBusiEnd.Enable 		= true;   // 사업 종료년월		
		mskQuar1.Enable 		= true;   // 1분기
		mskQuar2.Enable 		= true;   // 2분기
		mskQuar3.Enable 		= true;   // 3분기
		mskQuar4.Enable 		= true;   // 4분기		
		mskAssProAmt.Enable 	= true;   // 자산취득비
		mskFacManaAmt.Enable 	= true;   // 시설관리 유지비		
		mskTransAmt.Enable 		= true;   // 이월금액		
		fileBtnRow.Enable 		= true;   // 파일 추가, 삭제 버튼	

		taBusiPrps.SetFocus();

	}else{
		btnDprtPop2.Enable 		= false;  // 상세화면 부서팝업 버튼		
		//cboAccountDetl.Enable 	= false;  // 회계구분 Combo		
		edBusiNm.Enable 		= false;  // 사업명		
		taBusiPrps.Enable 		= false;  // 추진배경 및 목적
		taBusiSmry1.Enable 		= false;  // 개요		
		calBusiStr.Enable 		= false;  // 사업 시작년월
		calBusiEnd.Enable 		= false;  // 사업 종료년월		
		mskQuar1.Enable 		= false;  // 1분기
		mskQuar2.Enable 		= false;  // 2분기
		mskQuar3.Enable 		= false;  // 3분기
		mskQuar4.Enable 		= false;  // 4분기		
		mskAssProAmt.Enable 	= false;  // 자산취득비
		mskFacManaAmt.Enable 	= false;  // 시설관리 유지비
		mskTransAmt.Enable 		= false;  // 이월금액		
		fileBtnRow.Enable 		= false;  // 파일 추가, 삭제 버튼
	}		
}


/*-------------------------------------------------------------------------------------------------+
>> 그리드 더블클릭시 기본정보 / 장비이력내역  조회되도록 한다.
+-------------------------------------------------------------------------------------------------*/

function fcGrdOnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{

	switch ( obj.id ) {

		//첨부파일 목록 
		case "grdUploadFile" :
			var sLocalFileName  = fcd_GetSelectFile("Save", dsUploadFile.GetColumn(nRow, "FILE_NM"));
			var sServerFileName = dsUploadFile.GetColumn(nRow, "FILE_PATH");
			var sFileSize       = dsUploadFile.GetColumn(nRow, "FILE_SIZE");
			if ( sLocalFileName == null ) return;

			fnc_FileRead(divBtnLst.HttpFile, sLocalFileName[0] + "\\" + sLocalFileName[1], sServerFileName, sFileSize, nRow, "PROG_BAR", dsUploadFile);

			break;

	}

}

// 20111028_장한너울 -- dsOutTeamNm 조회로 교체(combo 사용)
function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if ( obj.id == "dsOutTeamNm" ) 
	{
		dsOutTeamNm.InsertRow(0);
		dsOutTeamNm.SetColumn(0, "TEAM_NM", "전체");
		cboTeamNm.index = 0;
	}
}

