/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	if (!fcSearchCheck(obj)) return;// 필수조건 체크 및 그리드 초기화

	var sServiceName = "rbs1030-service:searchList";  // Grid 조회
	var sInDataSet   = "";
	var sOutDataSet  = "dsList=dsList ";     


	fcd_AddParam("BUSI_YEAR"    , cboYear.Value);        // 년도  	
	fcd_AddParam("ACC_BGN_CD"   , cboAccount.Value);     // 회계구분 


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);


}

