>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {

	// DataSet의 Header 정보가 있을 때  Add 처리
	// if ( fnc_DatasetChangeCheck(dsHeal) ) {
		// fnc_Message(L_COM_ALT_0024, "N", "현재 작업중인 행이 존재하기 때문에\r\n 행을 추가할 수 ");
		// return;
	// }

	dsList.AddRow(); // Record Add
	dsList.SetFocus(); // GRID에 ADD 한 ROW 의 첫번째 항목에 입력Focus를 준다.



	/*  필수입력값 셋팅
	 * 1. 시개금코드   - 저장할 때 SQL로 구한다.
	 */	 



	/*  
	 * 초기값 셋팅 : 
	 */    
	var nRow = dsList.currow;	

    dsList.SetColumn(nRow, "BUSI_YEAR"   , ParseInt(fcd_GetSysdate("YYYY")));	// 현재년도로 셋팅
    dsList.SetColumn(nRow, "CONFIRM_TMS" , '1');  // 1 차로 셋팅	
}


