/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;


	// 콤보 조회 후 
	//if ( sServiceName == "rbs2010-service:searchYearNum" ) {
		// 첫번째 항목이 선택되게 한다. 생성하거나 모든 데이타를 다 지우고 저장했을때
		// 콤보항목수가 변화가 있기 때문이다. 
	    //cboYearNum.Index = 0; 

	    // 조회
	    //fcSearch();	
	//}	

	// 조회 완료 후
	if ( sServiceName == "rbs1030-service:searchList" ) {

	}			

	// 입력마감 후
	if ( sServiceName == "rbs1030-service:saveInputEndY" ) {
		fcSearch();  // 다시 조회한다.
	}		

	// 마감해지 후
	if ( sServiceName == "rbs1030-service:saveInputEndN" ) {
		fcSearch();  // 다시 조회한다.
	}	


}


