/*-------------------------------------------------------------------------------------------------+
>>  활동년도 설정 2011 년~  
+-------------------------------------------------------------------------------------------------*/
function fcSetYear(){

	//	전체 추가
	//dsYear.InsertRow(0);		
	//dsYear.SetColumn(0,"CD"   ,"ALL" );
	//dsYear.SetColumn(0,"CD_NM","전체");


	// 년도 셋팅
	var sCurYear  = ParseInt(fcd_GetSysdate("YYYY"));

	for(var i=sCurYear; i >= sCurYear - 5; i--){
		var nRow = dsYear.AddRow();

		dsYear.SetColumn(nRow,"CD"   ,i);
		dsYear.SetColumn(nRow,"CD_NM",i);	
	}


	/*	20111021_장한너울_수정
	// 년도 셋팅, GRID 에서 연동할 년도 콤보 
	var sCurYearGrid  = ParseInt(fcd_GetSysdate("YYYY"));

	for(var i=sCurYearGrid; i >= sCurYearGrid - 5; i--){
		var nRow = dsYearGrid.AddRow();

		dsYearGrid.SetColumn(nRow,"CD"   ,i);
		dsYearGrid.SetColumn(nRow,"CD_NM",i);	
	}
	*/


}


/*-------------------------------------------------------------------------------------------------+
>>  사원, 부서 팝업 열기  
+-------------------------------------------------------------------------------------------------*/
function fcEmpDialoge(obj)
{
	var sFormId 	= "";
	var sArgs 		= "";
	var arrReturn   = array(4);

	sFormId = "common::comDeptTree.xml";
	sArgs = "";	

	arrReturn = Dialog(sFormId, sArgs, 334, 443, "");

	//alert(arrReturn);


	// 담당자ID, 담당자이름, 부서코드, 부서명 셋팅
	var nRow = dsList.currow;

	dsList.SetColumn(nRow, "MNG_ID"   , arrReturn[0]);	
	dsList.SetColumn(nRow, "MNG_NAME" , arrReturn[1]);
	dsList.SetColumn(nRow, "DPRT_CD"  , arrReturn[4]);
	dsList.SetColumn(nRow, "DPRT_NAME", arrReturn[5]);

}


