/*-------------------------------------------------------------------------------------------------+
>>  [입력마감] 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveInputEndY(obj) {	

	if ( !fcSaveCheck() ) return;

	var sServiceName = "rbs1030-service:saveInputEndY";
	var sInDataSet   = "dsList=dsList:u ";
	var sOutDataSet  = "dsList=dsList ";

	fcd_AddParam("INPUT_END_YN", "Y");


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}



/*-------------------------------------------------------------------------------------------------+
>>  [마감해지] 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveInputEndN(obj) {

	if ( !fcSaveCheck() ) return;

	var sServiceName = "rbs1030-service:saveInputEndN";
	var sInDataSet   = "dsList=dsList:u ";
	var sOutDataSet  = "dsList=dsList ";

	fcd_AddParam("INPUT_END_YN", "N");


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


/*-------------------------------------------------------------------------------------------------+
>>  [발송] 버튼  
+-------------------------------------------------------------------------------------------------*/
function fcBtnOnClick(obj)
{
	  switch ( obj.id ) {		

			case "btnSendSms" :	//SMS 발송			

				var iSelectRowCount = fnc_GetColumnValueCount(dsList, "CHK", "1");

				if ( iSelectRowCount <= 0 ) {
					fnc_Message(SNIS_CRA_Z003, "N");
					return false;
				}

				var sSendPhoneNumber 	= gdsUser.GetColumn(0,"HP_NO");	 //송신자이동전화번호 
				var sSendUserId 	 	= gdsUser.GetColumn(0,"USER_ID");//송신자 사번
				var sSendUserName 		= gdsUser.GetColumn(0,"USER_NM");//송신자명 
				var sReceivePhoneNumber = "";							 //수신자이동전화번호 , 여러명인경우 ; 로 구분 
				var sReceiveName 		= "";							 //수신자이동전화번호 , 여러명인경우 ; 로 구분 
				var sReceiveId 		= "";							 //수신자이동전화번호 , 여러명인경우 ; 로 구분 
				var sParams				= "";

				for(var i = 0; i <dsList.GetRowCount();i++){
					if(dsList.GetColumn(i,"CHK") == "1"){

						sReceivePhoneNumber +=  dsList.GetColumn(i,"MNG_HP_NO")+";";

						sReceiveName +=  dsList.GetColumn(i,"MNG_NAME")+";";
						sReceiveId +=  dsList.GetColumn(i,"MNG_ID")+";";

					}			

				}

				sReceivePhoneNumber = substr(sReceivePhoneNumber,0,LENGTH(sReceivePhoneNumber)-1);
				sReceiveName = substr(sReceiveName,0,LENGTH(sReceiveName)-1);


				sParams = "sSendPhoneNumber="+quote(sSendPhoneNumber);
				sParams += " sSendUserId="+quote(sSendUserId);
				sParams += " sSendUserName="+quote(sSendUserName);
				sParams += " sReceivePhoneNumber="+quote(sReceivePhoneNumber);
				sParams += " sReceiveName="+quote(sReceiveName);
				sParams += " sReceiveId="+quote(sReceiveId);

				nHandle = Open("common::comSendSms.xml", sParams, 240, 328, "Title=false");

				break;


		case "btnSendMsg" :	//쪽지 전송			

				var iSelectRowCount = fnc_GetColumnValueCount(dsList, "CHK", "1");

				if ( iSelectRowCount <= 0 ) {
					fnc_Message(SNIS_CRA_Z003, "N");
					return false;
				}

				var sSendUser  	= gdsUser.GetColumn(0,"USER_ID");	 //송신자사번 
				var sRecvUser   = "";//수신자사번
				var sTitle   	= "사업 모니터링";//제목 
				var sLinkUrl 	= "";							 //
				var sDesc   	= "";							 //
				var sCompId     = "";

				for(var i = 0; i <dsList.GetRowCount();i++){
					if(dsList.GetColumn(i,"CHK") == "1"){

						sRecvUser +=  dsList.GetColumn(i,"MNG_ID")+",";					

					}			

				}

				sRecvUser = substr(sRecvUser,0,LENGTH(sRecvUser)-1);

				sParams = "sSendUser="+quote(sSendUser);
				sParams += " sRecvUser="+quote(sRecvUser);
				sParams += " sTitle="+quote(sTitle);
				sParams += " sLinkUrl="+quote(sLinkUrl);
				sParams += " sDesc="+quote(sDesc);
				sParams += " sCompId="+quote(sCompId);

				//trace("sParams : " + sParams);

				nHandle = Open("common::comSendMessage.xml", sParams, 192, 552, "Title=false");

				//trace("nHandle : " + nHandle);

				break;
	 }
}

// 부진사유보기
function grdList_OnExpandUp(obj,nRow,nCell,strVal,nPivotIndex)
{
	var deviceInfo = split(GetDeviceInfo(), ";");
	var screen = split(deviceInfo[2], "x");
	var iXPos = (parseInt(screen[0]) - parseInt(270))/2;
	var iYPos = (parseInt(screen[1]) - parseInt(210))/2;
	pdivRmk.TrackPopup(iXPos,iYPos);
}

function pdivRmk_btnCancel_OnClick(obj)
{
	pdivRmk.ClosePopup();
}
