function fcSearch(obj) {
	if (!fcSearchCheck(obj)) return;// ÇÊ¼öÁ¶°Ç Ã¼Å© ¹× ±×¸®µå ÃÊ±âÈ­

	var sServiceName = "rbs1120-service:searchWebNotice";  // Grid Á¶È¸
	var sInDataSet   = "";
	var sOutDataSet  = "dsWebNotice=dsWebNotice";     

	fcd_AddParam("MEET_CD"  , rdoMeetCd.Value);        	// °æ·û/°æÁ¤ ±¸ºÐ
	fcd_AddParam("STND_YEAR", cbStndYear.Value);       // ±âÁØ³âµµ

	if (tab0.TabIndex == 1) {
		sServiceName = "rbs1120-service:searchHnrRefreRecv";  // Grid Á¶È¸
		sInDataSet   = "";
		sOutDataSet  = "dsHnrRefreReq=dsHnrRefreReq";     
	}
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}
function fcSearchHnrRefreReq(obj) {
	if (!fcSearchCheck(obj)) return;// ÇÊ¼öÁ¶°Ç Ã¼Å© ¹× ±×¸®µå ÃÊ±âÈ­

	var sServiceName = "rbs1120-service:searchWebRecv";  // Grid Á¶È¸
	var sInDataSet   = "";
	var sOutDataSet  = "dsHnrRefreReqWeb=dsHnrRefreReqWeb";     	

	fcd_AddParam("WEB_PARENT_SEQ_ID", dsWebNotice.GetColumn(dsWebNotice.row, "WEB_PARENT_SEQ_ID"));        // °æ·û/°æÁ¤ ±¸ºÐ	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}
function fcSearchStndYear(obj) {

	var sServiceName = "rbs1110-service:searchYearList";  // Grid Á¶È¸
	var sInDataSet   = "";
