/*-------------------------------------------------------------------------------------------------+
>>  Çà»èÁ¦ ¹öÆ° Å¬¸¯ ãÁ Ã³¸®
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {

	if (obj.GetForm().id == "specBtnRow") {

		var dCnt = 0;
		for(var i = 0; i < dsHnrRefreReq.GetRowCount(); i++) {
			if(dsHnrRefreReq.GetColumn(i, "CHK") == 1) dCnt ++;
			if(dsHnrRefreReq.GetColumn(i, "WNR_CNT") > 0) {
				fnc_message("È®ÀÎ","ÀÌ¹Ì ¸í¿¹½ÉÆÇÀÌ ¼±Á¤µÈ Â÷¼ö´Â »èÁ¦°¡ ºÒ°¡´ÉÇÕ´Ï´Ù\n¼±Á¤ÀÚ¸¦ »èÁ¦ÈÄ Â÷¼ö »èÁ¦°¡ °¡´ÉÇÕ´Ï´Ù.", "N");
				return;
			}
		}
		if(fnc_Message(SNIS_COM_1010, "Y", dCnt) == 0) return false;	

		for(var i = dsHnrRefreReq.GetRowCount() -1; i >= 0 ; i--) {
			if(dsHnrRefreReq.GetColumn(i, "CHK") == 1) dsHnrRefreReq.deleterow(i);
		}
	}
}


