/*-------------------------------------------------------------------------------------------------+
>>  Transaction ÈÄ Ã³¸® ÇØ¾ß ÇÒ ³»¿ë!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// °øÅëÄÚµå Á¶È¸ ¿Ï·á ÈÄ
	if ( sServiceName == "common-service:searchCode" ) {
		// °øÅëÄÚµå Á¶È¸ÈÄ Ã³¸®(³âµµ¸¦ Á¶È¸ÇØ¾ß ÇÏ¹Ç·Î)
		//fcSearch();
	} else if ( sServiceName == "rbs1110-service:searchYearList" ) { //  ±âÁØ³âµµ Á¶È¸ÈÄ
		fcSearch();  // Á¶È¸ÇÑ´Ù.
	} else if ( sServiceName == "rbs1120-service:searchWebNotice" ) { //  ±âÁØ³âµµ Á¶È¸ÈÄ
		fcSearchHnrRefreReq();  // ÇØ´ç °øÁö»çÇ×¿¡ ´ëÇÑ ½ÅÃ»³»¿ªÀ» Á¶È¸ÇÑ´Ù.
	} else if ( sServiceName == "rbs1120-service:searchWebRecv" ) { // ¿Ï·á ÈÄ
		//Trace(dsHnrRefreReq.savexml());
	} else if ( sServiceName == "rbs1120-service:saveHnrRefreReq" ) { // ÀúÀå ¿Ï·á ÈÄ
		fcSearch();  // ´Ù½Ã Á¶È¸ÇÑ´Ù.
	}			


}


