/*-------------------------------------------------------------------------------------------------+
>>  ÀúÀå ÇÏ±âÀü¿¡ ÇÊ¼öÇ×¸ñ Ã¼Å©
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	// º¯°æµÈ ÀÚ·á°¡ ÀÖ´ÂÁö Ã¼Å©
	if ( !fnc_DatasetChangeCheck(dsHnrRefreReq)) {
		// ÀúÀå ÇÒ ÀÚ·á°¡ ¾øÀ½
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// ÇÊ¼öÇ×¸ñÃ¼Å©
	oDataSet          = dsHnrRefreReq;
	sMandatoryColID   = "MEET_CD|STND_YEAR|REQ_USER_NM|HP_NO";
	sMandatoryColName = "°æ·û°æÁ¤±¸ºÐ|Çà»ç³âµµ|°í°´¸í"|"ÈÞ´ëÆù¹øÈ£";
	oGrid             = grdHnrRefreTms;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}

	return true;
}


