/*-------------------------------------------------------------------------------------------------+
>>  °ü¸®ÀÚ´Â °æ·û °æÁ¤ ÀüºÎ °ü¸®ÇÒ ¼ö ÀÖÀ¸³ª, ÇØ´ç ºÎ¼­Á÷¿øÀº °æ·û/°æÁ¤ ±¸ºÐ ¼±ÅÃ ºÒ°¡
+-------------------------------------------------------------------------------------------------*/
function fcCheckAuthority(obj)
{
	if (IsExistVar("sApro", true)) {
		if (sApro == "T") rdoMeetCd.Enable = true;
		else rdoMeetCd.Enable = false;
    }
}


function fcDsOnRowPosChanged(obj,nOldRow,nRow)
{
	if (obj.id == "dsWebNotice") {
		fcSearchHnrRefreReq(obj);
	}
}

function fcBtnOnClick(obj)
{
	if(obj.id == "btnConvWebHnrRefreReq") {
		fcConvWebHnrRefreReq(obj);
	}
}

function fcCheckCombo(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (obj.id == "cbStndYear") {
		fcSearch();
	}
}

function fcTabOnChanged(obj,nOldIndex,nNewindex)
{
	var bVisible = false;
	if (obj.id == "Tab0") {
		if (nNewindex == 0) {
			bVisible = false;
		} else if (nNewindex == 1) {
			bVisible = true;
		}
		specBtnRow.Visible = bVisible;	
		btnConvWebHnrRefreReq.Visible = !bVisible;
	}
	if (nNewindex == 1) {
		if (dsHnrRefreReq.rowcount() == 0) fcSearch(obj);
	}
}

function fcOnSize(obj,nCx,nCy,nState)
{
	fcSetResize(obj,nCx);
}


// Æ¯Á¤ÄÞÆ÷³ÍÆ®(±×¸®µå) ¸®»çÀÌÁî
function fcSetResize(obj,nX)
{	// ÇØ´çÈ­¸éÀº ±âÁØ»çÀÌÁîº¸´Ù Height°¡ Å©±â¶§¹®¿¡ ½ºÅ©·Ñ¹Ù »ý¼º¹ß»ý.±×¿¡ µû¶ó ±×¸®µå»çÀÌÁî Á¶Á¤

	var nWidth = nX-18;
	if( nX <= 820){
		Tab0.Width = 802;
		tab0.tab1.grdHnrRefreReq.AutoFit = false;
		tab0.tab1.grdHnrRefreReq.width = 578;
		tab0.tab2.grdHnrRefreReq.AutoFit = false;
		tab0.tab2.grdHnrRefreReq.width = 794;
		Shape1.Width = 801;	
		divBtnLst.Left = this.width - 405;	
		btnConvWebHnrRefreReq.left = 700;
		return;
	}else{
		Tab0.Width = nWidth - 10;
		tab0.tab1.grdHnrRefreReq.AutoFit = true;
		tab0.tab1.grdHnrRefreReq.width = nWidth - tab0.tab1.grdHnrRefreReq.left - 10;
		tab0.tab2.grdHnrRefreReq.AutoFit = true;
		tab0.tab2.grdHnrRefreReq.width = nWidth;
	}

	// °øÅë
	Shape1.Width = nWidth-15;
	specBtnRow.left = nWidth - 50;	
	divBtnLst.Left = nWidth - 380;	
	btnConvWebHnrRefreReq.left = nWidth - btnConvWebHnrRefreReq.width - 20;
}
