/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    //sBUTTONLIST   = 'FT'+sInpt+'FTFT';     //초기화,조회,저장,삭제,엑셀,인쇄,닫기
    sBUTTONLIST   = 'FT'+sInpt+'FTFT'; 

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	//fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	rdoMeetCd.Value = gdsUser.GetColumn(0, "CRA_CD");
	fcCheckAuthority(this);

    //공통코드 조회
	fcd_GetComboDs(dsInComCd);

	// 화면 열리자마자 기준년도 조회
	fcSearchStndYear();

	rdoMeetCd.Value  = sMeetCd;        	// 경륜/경정 구분
	cbStndYear.Value = sStndYear;       // 기준년도

}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "";
	if (   !fnc_FormUnloadCheck(sDataset  , sFormId) ) return false;
}

/***************************************************************************************************
*                                 상단 공통 버튼 Event 처리 부분                                   *
*                           초기화, 조회, 저장, 삭제, 엑셀, 인쇄, 닫기                             *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  초기화 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
	if (tab0.TabIndex == 0) dsHnrRefreReqWeb.ClearData();
	else dsHnrRefreReq.ClearData();  
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
	if (!fcSearchCheck(obj)) return;// 필수조건 체크 및 그리드 초기화

	var sServiceName = "rbs1120-service:searchWebNotice";  // Grid 조회
	var sInDataSet   = "";
	var sOutDataSet  = "dsWebNotice=dsWebNotice";     

	fcd_AddParam("MEET_CD"  , rdoMeetCd.Value);        	// 경륜/경정 구분
	fcd_AddParam("STND_YEAR", cbStndYear.Value);       // 기준년도

	if (tab0.TabIndex == 1) {
		sServiceName = "rbs1120-service:searchHnrRefreRecv";  // Grid 조회
		sInDataSet   = "";
		sOutDataSet  = "dsHnrRefreReq=dsHnrRefreReq";     
	}
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}
function fcSearchHnrRefreReq(obj) {
	if (!fcSearchCheck(obj)) return;// 필수조건 체크 및 그리드 초기화

	var sServiceName = "rbs1120-service:searchWebRecv";  // Grid 조회
	var sInDataSet   = "";
	var sOutDataSet  = "dsHnrRefreReqWeb=dsHnrRefreReqWeb";     	

	fcd_AddParam("WEB_PARENT_SEQ_ID", dsWebNotice.GetColumn(dsWebNotice.row, "WEB_PARENT_SEQ_ID"));        // 경륜/경정 구분	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}
function fcSearchStndYear(obj) {

	var sServiceName = "rbs1110-service:searchYearList";  // Grid 조회
	var sInDataSet   = "";
	var sOutDataSet  = "dsYear=dsYear";     

	fcd_AddParam("MEET_CD"      , rdoMeetCd.Value);        // 경륜/경정 구분

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}


/***************************************************************************************************
*                          CALL BACK 처리 부분                                                     *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회 완료 후
	if ( sServiceName == "common-service:searchCode" ) {
		// 공통코드 조회후 처리(년도를 조회해야 하므로)
		//fcSearch();
	} else if ( sServiceName == "rbs1110-service:searchYearList" ) { //  기준년도 조회후
		fcSearch();  // 조회한다.
	} else if ( sServiceName == "rbs1120-service:searchWebNotice" ) { //  기준년도 조회후
		fcSearchHnrRefreReq();  // 해당 공지사항에 대한 신청내역을 조회한다.
	} else if ( sServiceName == "rbs1120-service:searchWebRecv" ) { // 완료 후
		//Trace(dsHnrRefreReq.savexml());
	} else if ( sServiceName == "rbs1120-service:saveHnrRefreReq" ) { // 저장 완료 후
		fcSearch();  // 다시 조회한다.
	}			


}


