/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/


/*-------------------------------------------------------------------------------------------------+
>>  Combo가 바뀌면 조회한다.  
>>                             년도,  회계구분, 차수구분
+-------------------------------------------------------------------------------------------------*/
function fcCombo_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (obj.id == "rdoMeetCd") fcSearchStndYear();	
}

/*-------------------------------------------------------------------------------------------------+
>>  관리자는 경륜 경정 전부 관리할 수 있으나, 해당 부서직원은 경륜/경정 구분 선택 불가
+-------------------------------------------------------------------------------------------------*/
function fcCheckAuthority(obj)
{
	if (IsExistVar("sApro", true)) {
		if (sApro == "T") rdoMeetCd.Enable = true;
		else rdoMeetCd.Enable = false;
    }
}


function fcDsOnRowPosChanged(obj,nOldRow,nRow)
{
	if (obj.id == "dsWebNotice") {
		fcSearchHnrRefreReq(obj);
	}
}

function fcBtnOnClick(obj)
{
	if(obj.id == "btnConvWebHnrRefreReq") {
		fcConvWebHnrRefreReq(obj);
	}
}

function fcCheckCombo(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (obj.id == "cbStndYear") {
		fcSearch();
	}
}

function fcTabOnChanged(obj,nOldIndex,nNewindex)
{
	if (obj.id == "Tab0") {
		fcSearch(obj);
	}
}


/*-------------------------------------------------------------------------------------------------+
>>  선택 : 확인 처리
+-------------------------------------------------------------------------------------------------*/
function fcChoice(obj)
{
	var dsObj;
	if (tab0.tabindex == 0) dsObj = dsHnrRefreReqWeb;
	else                    dsObj = dsHnrRefreReq;

	var nChkedIdx = dsObj.FindRow("CHK", 1);
	if(nChkedIdx==-1) {
		fcd_SetReturnDs(dsObj, dsEmp.GetCurrow());
	} else {
		var sRowIdx;
		for(var i=0; i<dsObj.GetRowCount(); i++) {
			if(dsObj.GetColumn(i, "CHK")==1) {
				sRowIdx += i + "^";
			}
		}
		sRowIdx = mid(sRowIdx, 0, length(sRowIdx)-1);
		fcd_SetReturnDs(dsObj, sRowIdx);
	}
	close(true);
}

/*-------------------------------------------------------------------------------------------------+
>>  취소 처리
+-------------------------------------------------------------------------------------------------*/
function fcClose(obj) {
	Close(false);     //Null 반환 후 종료
}
