/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
	if (!fcSearchCheck(obj)) return;// 필수조건 체크 및 그리드 초기화
	dsHnrRefreWnr.clear();

	var sServiceName = "rbs1110-service:searchHnrRefreTms";  // Grid 조회
	var sInDataSet   = "";
	var sOutDataSet  = "dsHnrRefreTms=dsHnrRefreTms";  	

	fcd_AddParam("MEET_CD"  , rdoMeetCd.Value);        	// 경륜/경정 구분
	fcd_AddParam("STND_YEAR", cbStndYear.Value);       	// 기준년도

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}
function fcSearchHnrRefreWnr(obj) {
	if (!fcSearchCheck(obj)) return;// 필수조건 체크 및 그리드 초기화

	var sServiceName = "rbs1130-service:searchHnrRefreWnr";  // Grid 조회
	var sInDataSet   = "";
	var sOutDataSet  = "dsHnrRefreWnr=dsHnrRefreWnr";     	

	fcd_AddParam("MEET_CD"  , dsHnrRefreTms.GetColumn(dsHnrRefreTms.row, "MEET_CD"));        	// 경륜/경정 구분
	fcd_AddParam("STND_YEAR", dsHnrRefreTms.GetColumn(dsHnrRefreTms.row, "STND_YEAR"));       	// 기준년도
	fcd_AddParam("TMS",       dsHnrRefreTms.GetColumn(dsHnrRefreTms.row, "TMS"));       		// 차수

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}
function fcSearchStndYear(obj) {

	var sServiceName = "rbs1110-service:searchYearList";  // Grid 조회
	var sInDataSet   = "";
	var sOutDataSet  = "dsYear=dsYear";     

	fcd_AddParam("MEET_CD"      , rdoMeetCd.Value);        // 경륜/경정 구분

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {
	if ( !fcSaveCheck() ) return;

	var sServiceName = "rbs1130-service:saveHnrRefreWnr";
	var sInDataSet   = "dsHnrRefreWnr=dsHnrRefreWnr:u";
	var sOutDataSet  = "dsHnrRefreWnr=dsHnrRefreWnr";

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

