/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
    var nRow = 0;
	if (obj.GetForm().id == "specBtnRow") {
		if (dsHnrRefreWnr.ColCount() == 0) {
			fnc_message("먼저 조회후 작업하세요!", "N");
			return;
		}

		var args = "sMeetCd="     + quote(rdoMeetCd.Value)  + " " + "sStndYear=" + quote(cbStndYear.Value);	
		var bRtn = Dialog("frm_rbs::RBS1120P.xml", args, 816, 576, "");
		if(bRtn) {	
			for (var iCnt=0;iCnt<G_ds_return.RowCount();iCnt++) {
				var nRow = 0;
				nRow = dsHnrRefreWnr.AddRow();
				dsHnrRefreWnr.SetColumn(nRow, "MEET_CD",   		dsHnrRefreTms.GetColumn(dsHnrRefreTms.row, "MEET_CD"));
				dsHnrRefreWnr.SetColumn(nRow, "STND_YEAR", 		dsHnrRefreTms.GetColumn(dsHnrRefreTms.row, "STND_YEAR"));
				dsHnrRefreWnr.SetColumn(nRow, "TMS",    		dsHnrRefreTms.GetColumn(dsHnrRefreTms.row, "TMS"));
				dsHnrRefreWnr.SetColumn(nRow, "REQ_STND_YEAR",  G_ds_return.GetColumn(iCnt, "STND_YEAR"));
				dsHnrRefreWnr.SetColumn(nRow, "REQ_SEQ_NO",     G_ds_return.GetColumn(iCnt, "SEQ_NO"));
				dsHnrRefreWnr.SetColumn(nRow, "INST_DT",        G_ds_return.GetColumn(iCnt, "INST_DT"));
				dsHnrRefreWnr.SetColumn(nRow, "REQ_USER_NM",    G_ds_return.GetColumn(iCnt, "REQ_USER_NM"));
				dsHnrRefreWnr.SetColumn(nRow, "HP_NO",     		G_ds_return.GetColumn(iCnt, "HP_NO"));
				dsHnrRefreWnr.SetColumn(nRow, "AGE",     		G_ds_return.GetColumn(iCnt, "AGE"));
				dsHnrRefreWnr.SetColumn(nRow, "SEX",     		G_ds_return.GetColumn(iCnt, "SEX"));
				dsHnrRefreWnr.SetColumn(nRow, "BRNC_CD",     	G_ds_return.GetColumn(iCnt, "BRNC_CD"));
				dsHnrRefreWnr.SetColumn(nRow, "WIN_CNT",     	G_ds_return.GetColumn(iCnt, "WIN_CNT"));				
				dsHnrRefreWnr.SetColumn(nRow, "INST_MTH",       G_ds_return.GetColumn(iCnt, "INST_MTH"));
			}
		}		

	}		
}


