/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {

	if (obj.GetForm().id == "specBtnRow") {
		if (dsHnrRefreWnr.ColCount() == 0) {
			fnc_message("먼저 조회후 작업하세요!", "N");
			return;
		}

		var dCnt = 0;
		for(var i = 0; i < dsHnrRefreWnr.GetRowCount(); i++) {
			if(dsHnrRefreWnr.GetColumn(i, "CHK") == 1) dCnt ++;
			if(dsHnrRefreWnr.GetColumn(i, "WNR_CNT") > 0) {
				fnc_message("확인","이미 명예심판이 선정된 차수는 삭제가 불가능합니다\n선정자를 삭제후 차수 삭제가 가능합니다.", "N");
				return;
			}
		}
		if(fnc_Message(SNIS_COM_1010, "Y", dCnt) == 0) return false;	

		for(var i = dsHnrRefreWnr.GetRowCount() -1; i >= 0 ; i--) {
			if(dsHnrRefreWnr.GetColumn(i, "CHK") == 1) dsHnrRefreWnr.deleterow(i);
		}
	}
}


/***************************************************************************************************
*                          CALL BACK 처리 부분                                                     *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회 완료 후
	if ( sServiceName == "common-service:searchCode" ) {
		// 공통코드 조회후 처리(년도를 조회해야 하므로)
		//fcSearch();
	} else if ( sServiceName == "rbs1110-service:searchYearList" ) { //  기준년도 조회후
		fcSearch();  // 조회한다.
	} else if ( sServiceName == "rbs1110-service:searchHnrRefreTms" ) { //  차수 조회
		if (dsHnrRefreTms.RowCount() == 0) {
			fnc_message("행사계획이 존재하지 않습니다. 행사계획을 먼저 등록하세요!","N");
			specBtnRow.Enable = false;
			return;
		} else {
			specBtnRow.Enable = true;
		}
		fcSearchHnrRefreWnr();  // 해당 공지사항에 대한 신청내역을 조회한다.
	} else if ( sServiceName == "rbs1130-service:searchHnrRefreWnr" ) { //  선정자 조회 완료 후
		//Trace(dsHnrRefreWnr.savexml());
		fnc_SetStatus(SNIS_CRA_0003, dsHnrRefreWnr.rowcount());
	} else if ( sServiceName == "rbs1130-service:saveHnrRefreWnr" ) { // 저장 완료 후
		fcSearchHnrRefreWnr();  // 다시 조회한다.
	}			
}


/***************************************************************************************************
*                            상단 공통 버튼 Event  보조 함수                                       *
*                                                                                                  *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    fcInit(obj);  // 그리드 초기화

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	// 변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsHnrRefreWnr)) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	oDataSet          = dsHnrRefreWnr;
	sMandatoryColID   = "MEET_CD|STND_YEAR|TMS";
	sMandatoryColName = "경륜경정구분|행사년도|차수";
	oGrid             = grdHnrRefreWnr;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}

	return true;
}


