/*-------------------------------------------------------------------------------------------------+
>>  Combo가 바뀌면 조회한다.  
>>                             년도,  회계구분, 차수구분
+-------------------------------------------------------------------------------------------------*/
function fcCombo_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (obj.id == "rdoMeetCd") fcSearchStndYear();	
}

/*-------------------------------------------------------------------------------------------------+
>>  관리자는 경륜 경정 전부 관리할 수 있으나, 해당 부서직원은 경륜/경정 구분 선택 불가
+-------------------------------------------------------------------------------------------------*/
function fcCheckAuthority(obj)
{
	if (IsExistVar("sApro", true)) {
		if (sApro == "T") rdoMeetCd.Enable = true;
		else rdoMeetCd.Enable = false;
    }
}


function fcDsOnRowPosChanged(obj,nOldRow,nRow)
{
	if (obj.id == "dsHnrRefreTms") {
		fcSearchHnrRefreWnr(obj);
	}
}


function fcCheckCombo(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (obj.id == "cbStndYear") {
		fcSearch();
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  문자메세지 전송
+-------------------------------------------------------------------------------------------------*/
function fcBtnOnClick(obj)
{
	switch ( obj.id ) {		

		case "btnSendSms" :	//SMS 전송		
			var iSelectRowCount = fnc_GetColumnValueCount(dsHnrRefreWnr, "CHK", "1");

			if ( iSelectRowCount <= 0 ) {
				fnc_Message(SNIS_CRA_Z003, "N");
				return false;
			}

			var sSendPhoneNumber 	= gdsUser.GetColumn(0,"HP_NO");	 //송신자이동전화번호 
			var sSendUserId 	 	= gdsUser.GetColumn(0,"USER_ID");//송신자 사번
			var sSendUserName 		= gdsUser.GetColumn(0,"USER_NM");//송신자명 
			var sReceivePhoneNumber = "";							 //수신자이동전화번호 , 여러명인경우 ; 로 구분 
			var sReceiveName 		= "";							 //수신자이동전화번호 , 여러명인경우 ; 로 구분 
			var sParams				= "";
			var sReceiveId          = "";
			var sTitle              = "명예심판 선정자 문자 발송";
			var sPermTempGbn        = "002";						// 수신자가 정규직인지 여부

			for(var i = 0; i <dsHnrRefreWnr.GetRowCount();i++){
				if(dsHnrRefreWnr.GetColumn(i,"CHK") == "1"){

					sReceivePhoneNumber +=  dsHnrRefreWnr.GetColumn(i,"HP_NO")+";";
					sReceiveName        +=  dsHnrRefreWnr.GetColumn(i,"REQ_USER_NM")+";";
					sReceiveId          +=  "-"+";";
				}			

			}

			sReceivePhoneNumber = substr(sReceivePhoneNumber,0,LENGTH(sReceivePhoneNumber)-1);
			sReceiveName = substr(sReceiveName,0,LENGTH(sReceiveName)-1);
			sReceiveId = substr(sReceiveId,0,LENGTH(sReceiveId)-1);

			sParams = "sSendPhoneNumber="+quote(sSendPhoneNumber);
			sParams += " sSendUserId="+quote(sSendUserId);
			sParams += " sSendUserName="+quote(sSendUserName);
			sParams += " sReceivePhoneNumber="+quote(sReceivePhoneNumber);
			sParams += " sReceiveName="+quote(sReceiveName);
			sParams += " sReceiveId="+quote(sReceiveId);
			sParams += " sTitle="+quote(sTitle);
			sParams += " sRecvrUserType="+quote(sPermTempGbn);
			nHandle = Open("common::comSendSms.xml", sParams, 240, 328, "Title=false");


			break;
	 }
}

function fcOnSize(obj,nCx,nCy,nState)
{
	fcSetResize(obj,nCx);
}


// 특정콤포넌트(그리드) 리사이즈
function fcSetResize(obj,nX)
{	// 해당화면은 기준사이즈보다 Height가 크기때문에 스크롤바 생성발생.그에 따라 그리드사이즈 조정
	var arrObj = array(-1);
	arrObj[0] = "grdHnrRefreWnr";

	var nWidth = nX-18;
	if( nX <= 820){
		// for( var i=0; i<length(arrObj); i++){
			// object(arrObj[i]).Autofit = false;		
			// object(arrObj[i]).right = 788;
			// object(arrObj[i]).Redraw;
		// }
		grdHnrRefreWnr.width = 586;
		grdHnrRefreWnr.AutoFit = false;
		Shape1.Width = 801;	
		divBtnLst.Left = this.width - 405;			
		return;
	}else{
		grdHnrRefreWnr.AutoFit = true;
		grdHnrRefreWnr.Width = nWidth -10 - grdHnrRefreWnr.left;
	}

	// 공통
	Shape1.Width = nWidth-15;
	specBtnRow.left = nWidth - 50;	
	divBtnLst.Left = nWidth - 380;	
	btnSendSms.left = nWidth - btnSendSms.width - 20;
}
