var sFormId       = this.GetForm().id;
var sBUTTONLIST   = 'FFFFTFT';                         //공통기능정의(버튼순으로 T or F)
var sUSERAUTH     = '';                                //사용자별 프로그램 사용 권한
var sFORMCAPTION  = '';                                //Form의 Title 정보
var sFORMLOCATION = '';                                //Form의 Location 정보
var sUSERLAVEL    = '';                                //Form의 사용  Level
var vREVOBJ       = Array(9);                          //결과값 처리를 위한 오브젝트 
var sGRDTIT       = "";                                //그리드 제목
var sCUROBJ       = "";                                //현재 LOST FOCUS 오브젝트

var sMenuGrpNum     = "0";                          	   // 메뉴그룹의 갯수보관
var sMenuListNum    = "0";                             // 메뉴리스트의 갯수보관
var sMenuUpId       = "";
var sMenuUpNm       = "";
var sMsg            = "";								// 삭제 불가능 row 보관

var sCurDate = fcd_GetSysdate("YYYYMMDD");				// 시스템의 오늘날짜
var sCurYear = substr(sCurDate, 0, 4);					// 시스템의 현재년도
var sTonerCd = "002";

/***************************************************************************************************
*                                         Form 처리 부분                                           *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'TTFFTFT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

    //공통코드 조회
    fcd_GetComboDs(dsInComCd);

    //조회 조건의 날짜(올해 1월1일 ~ today)
    calFromDt.Value = sCurYear + "0101";
    calToDt  .Value = sCurDate;

    //조회 조건의 승인여부에는 '전체'를 추가한다.
    if( dsProgStatCdSearch.rowcount > 0 ) {
		dsProgStatCdSearch.InsertRow(0);
		dsProgStatCdSearch.SetColumn(0, "CD_NM", "전체");
		cboProgStatCd      .index = 0;
		cboProgStatCdSearch.index = 0;
	}
	if (isExistVar("sApro", true) && isExistVar("sMnAuthRmk", true)) {
		if (sApro == "T" && length(trim(sMnAuthRmk)) > 0) {
			rdoGbn.Value = sMnAuthRmk;
			dsSupplCd.Filter("CD_TERM1='"+rdoGbn.Value+"'");
			rdoGbn.Enable = false;
		}
	}

	fcSearch();	//시작시 조회
}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는 DataSet Object를 넘긴다.
	var sDataset = "dsSupplReq|dsSupplRegi";
	if ( !fnc_FormUnloadCheck(sDataset, sFormId) ) return false;
}



/***************************************************************************************************
*                                      업무화면 공통 버튼 Event 처리 부분                          *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  초기화 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
	dsSupplReq .ClearData();
	dsSupplRegi.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
	fcInit(obj);
	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rbs3030-service:searchSupplReq";
    var sInDataSet   = "";
    var sOutDataSet  = "dsSupplReq=dsSupplReq ";  

    //조회조건
	fcd_AddParam("REQ_DT_FROM" , calFromDt.Value);
	fcd_AddParam("REQ_DT_TO"   , calToDt.Value);
	fcd_AddParam("PROG_STAT_CD", cboProgStatCdSearch.Value);
	fcd_AddParam("BIZ_GBN"     , rdoGbn.Value);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

function fcRegiSearch(obj) { 
	var sServiceName = "rbs3030-service:searchSupplRegi";
    var sInDataSet   = "";
    var sOutDataSet  = "dsSupplRegi=dsSupplRegi ";  

    if( dsSupplReq.currow >= 0 ) { 
		//조회조건
		fcd_AddParam("REQ_DT"  , dsSupplReq.GetColumn(dsSupplReq.currow, "REQ_DT"));
		fcd_AddParam("REQ_ID", dsSupplReq.GetColumn(dsSupplReq.currow, "REQ_ID"));
		fcd_AddParam("SEQ"     , dsSupplReq.GetColumn(dsSupplReq.currow, "SEQ"));

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
	}
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

// 저장 전에 체크
function fcSaveCheck(obj) {  
	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsSupplReq) && !fnc_DatasetChangeCheck(dsSupplRegi)) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	oDataSet          = dsSupplReq;
	sMandatoryColID   = "REQ_DT|REQ_ID|REQ_CNTNT";
	sMandatoryColName = "신청일자|사번|신청내용";
	oGrid             = grdSupplReq;

	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	// 필수항목체크
	oDataSet          = dsSupplRegi;
	sMandatoryColID   = "SUPPL_CD|REQ_QTY";
	sMandatoryColName = "소모품|신청수량";
	oGrid             = grdSupplRegi;

	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}	

	//소모품 수량  -1 입력 할 수 있도록 변경 
	/*
	for( var i = 0 ; i < dsSupplRegi.rowcount ; i++) {
		 if( dsSupplRegi.GetColumn(i, "REQ_QTY") < 0 ) {
			fnc_Message(SNIS_RBM_E006, "N", i+1, "신청수량");
			return false;
		 }
	}*/



    switch ( obj.id ) {
		case "btnSave" :	//저장버튼 


			break;

		case "btnAprv" :	//승인버튼 
			if( dsSupplRegi.rowcount <= 0 ) {
				fnc_Message(SNIS_RBM_E008, "N");
				return false;
			}

			cboProgStatCd.Value="003"; //승인

			break;

		case "btnRtn" :		//반려버튼 
			if( dsSupplRegi.rowcount > 0 ) {
				fnc_Message(SNIS_RBM_E009, "N");
				return false;
			}
			cboProgStatCd.Value="009"; //반려 

			break;

		case "mgnBtn" :		//관리자 버튼 
			if( dsSupplRegi.rowcount <= 0 ) {
				fnc_Message(SNIS_RBM_E008, "N");
				return false;
			}

			cboProgStatCd.Value="003"; //승인

			break;


	}


	return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck(obj) ) return;   

    if(fnc_Message(SNIS_COM_1012, "Y")) {           
		var sServiceName = "rbs3030-service:saveSupplReqRegi";
		var sInDataSet   = "dsSupplReq=dsSupplReq:u dsSupplRegi=dsSupplRegi:u";
		var sOutDataSet  = "dsSupplReq=dsSupplReq dsSupplRegi=dsSupplRegi";  	

		fcd_AddParam("BIZ_GBN"     , rdoGbn.Value);

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

    }
}

