/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {

	switch ( obj.GetForm().id ) {
		case "reqBtnRow" :	//소모품 신청 추가
			for(var i = 0; i < dsSupplReq.rowcount; i++) {
				if( dsSupplReq.GetRowType(i) == "insert" ) {
					fnc_Message(SNIS_COM_1051, "N"); 
					return;
				}
			}

			var nRow = dsSupplReq.AddRow();

			//자동 Setting
			dsSupplReq.SetColumn(nRow, "REQ_DT"      , sCurDate);
			dsSupplReq.SetColumn(nRow, "REQ_ID"    , gdsUser.GetColumn(gdsUser.currow, "USER_ID"));
			dsSupplReq.SetColumn(nRow, "USER_NAME"   , gdsUser.GetColumn(gdsUser.currow, "USER_NM"));
			dsSupplReq.SetColumn(nRow, "MNG_REQ_YN"  , "N");	//관리자신청
			dsSupplReq.SetColumn(nRow, "PROG_STAT_CD", "001");	//신청으로 Setting
			dsSupplReq.SetColumn(nRow, "BIZ_GBN", rdoGbn.Value);	// 소모품 분류

			taReqCntnt.SetFocus();// 입력 첫 항목으로 Focus
			break;

		case "regiBtnRow" :	// 소모품 신청 수량 추가

			if( dsSupplReq.currow >= 0 ) { //소모품신청현황이 선택된 상태에서만 신청수량 추가 가능
				nRow = dsSupplRegi.AddRow();
				dsSupplRegi.SetColumn(nRow, "REQ_QTY" , "0");
				dsSupplRegi.SetColumn(nRow, "REQ_DT"  , dsSupplReq.GetColumn(dsSupplReq.currow, "REQ_DT"));
				dsSupplRegi.SetColumn(nRow, "REQ_ID"  , dsSupplReq.GetColumn(dsSupplReq.currow, "REQ_ID"));
				dsSupplRegi.SetColumn(nRow, "SEQ"     , dsSupplReq.GetColumn(dsSupplReq.currow, "SEQ"));

				grdSupplRegi.SetCellPos(1);
				grdSupplRegi.SetFocus();
			} else {	
				fnc_Message(SNIS_RBM_A003, "N", "소모품신청현황", "소모품");
			}
			break;
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {

	switch( obj.GetForm().id ) {
		case "reqBtnRow" :
			if ( !fnc_DeleteCheck(dsSupplReq, "CHK", "1") ) return;
			break;

		case "regiBtnRow" :
			if( dsSupplReq.GetColumn(dsSupplReq.currow, "SIGN_DT").length > 0 ) {
				fnc_Message(SNIS_RBM_E011, "N");
				return;
			}
			if ( !fnc_DeleteCheck(dsSupplRegi, "CHK", "1") ) return;
			break;
	}
}


