*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if ( sServiceName == "rbs3030-service:searchSupplReq" ) {

	}

	// 조회 완료 후
	if ( sServiceName == "rbs3030-service:searchQty" ) {	
		var nQty = 0;

		// 토너 신청시에는 재고 체크 안함
		if (rdoGbn.Value == sTonerCd) {
			if( dsQty.rowcount == 1 ) nQty = dsQty.GetColumn(0, "QTY");
			dsSupplRegi.SetColumn(dsSupplRegi.currow, "STOCK_QTY", nQty);
			return;
		}

		//선택한 품목의 재고가 없다면 등록할 수 없도록 한다.

		if( dsQty.rowcount == 1 ) {
			nQty = dsQty.GetColumn(0, "QTY");

			if( nQty > 0 ) {
				dsSupplRegi.SetColumn(dsSupplRegi.currow, "STOCK_QTY", nQty);
				dsSupplRegi.SetColumn(dsSupplRegi.currow, "REQ_QTY"  , "0");
			} else {	//재고가 1보다 작을 떄
				dsSupplRegi.SetColumn(dsSupplRegi.currow, "SUPPL_CD", "");
				fnc_Message(SNIS_RBM_E005);
				return;
			}
		} else {	//재고가 없을 때
			if( dsSupplRegi.GetColumn(dsSupplRegi.currow, "SUPPL_CD").length > 0 ) {
				dsSupplRegi.SetColumn(dsSupplRegi.currow, "SUPPL_CD", "");
				fnc_Message(SNIS_RBM_E005);
				return;
			}
		}
	}

	// 저장 완료 후
	if ( sServiceName == "rbs3030-service:saveSupplReqRegi" ) {

		fcSendMngMsg();

		fcSearch();		
	}	
}

