/*-------------------------------------------------------------------------------------------------+
>>  Column의 값이 변경된 경우
+-------------------------------------------------------------------------------------------------*/
function fcDsOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	switch ( obj.id ) {
		case "dsSupplRegi" :	
			if( strColumnID == "SUPPL_CD" ) { // 선택된 재고품목의 수량 가져오기
				var sServiceName = "rbs3030-service:searchQty";
				var sInDataSet   = "";
				var sOutDataSet  = "dsQty=dsQty ";  

				//조회조건
				fcd_AddParam("SUPPL_CD" , varNewValue);

				fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
			}			
			break;

		case "dsSupplReq" :
			if( strColumnID == "PROG_STAT_CD" ) {
				if( varNewValue == "001" ) {
					dsSupplReq.SetColumn(nRow, "APRV_DT", "");
				} else {
					dsSupplReq.SetColumn(nRow, "APRV_DT", sCurDate);
				}
			}
			break;
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  row가 값이 변경된 경우
+-------------------------------------------------------------------------------------------------*/
function fcDsOnRowPosChanged(obj,nOldRow,nRow)
{
	switch(obj.id) {
		case "dsSupplReq" :
			fcRegiSearch();	//신청품목 조회

			if( dsSupplReq.GetRowType(nRow) == "insert" ) {
				calReqDt  .Enable = true;
				taReqCntnt.Readonly = false;
				edUserNm  .Enable = true;
				btnPopSearch.Visible = true;
				mgnBtn.Visible = true;

				btnAprv.Visible = false;
				btnRtn.Visible = false;

				taReqCntnt.SetFocus();

			} else {
				calReqDt  .Enable = false;
				taReqCntnt.Readonly = true;
				edUserNm  .Enable = false;
				btnPopSearch.Visible = false;
				mgnBtn.Visible = false;

				btnAprv.Visible = true;
				btnRtn.Visible = true;
			}

			// 승인,반려된 건에 대해서는 변경, 삭제 불가능
			if( dsSupplReq.GetColumn(nRow, "SIGN_DT").length > 0) {
				taReply.Readonly = true;
				//cboProgStatCd.Enable = false;
			} else {
				taReply.Readonly = false;
				//cboProgStatCd.Enable = true;
			}
			break;
	}
}

