/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	//Return Key Press時 Next Component focus
	if ( nChar == ENTER_KEY ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}
	//Control Key가 눌려져 있다면 단축키 처리
	//if (bControl) fnc_HotKey(this, nChar);
}

function fcBtnOnClick(obj)
{
	//신청부터 서명까지 한번에 처리
	if( dsSupplReq.GetRowType(dsSupplReq.currow) != "insert" ) {
		fnc_Message(SNIS_RBM_E013, "N");
		return;
	}

	dsSupplReq.SetColumn(dsSupplReq.currow, "MNG_REQ_YN", "Y");	//관리자신청

	fcSave(obj);
}

function fcEdOnClick(obj)
{
	var sUrl 	= "common::comDeptTree.xml";
	var sArgs 		= "MULTY = N";	
	var arrReturn   = array(3);

	arrReturn = Dialog(sUrl, sArgs, 334, 443, "");

	if (fnc_IsNull(arrReturn) == false ) {
		dsSupplReq.SetColumn(dsSupplReq.currow, "REQ_ID" , arrReturn[0]);
		dsSupplReq.SetColumn(dsSupplReq.currow, "USER_NAME", arrReturn[1]);
	}
}

function fcDsCanRowPosChange(obj,nOldRow,nRow)
{
	switch( obj.id ) {
		case "dsSupplReq" :

			if( dsSupplReq.GetRowType(nOldRow) != "normal" ) {
				if( !fnc_Message(SNIS_COM_1054, "Y") ) {
					return false;
				}
			}
			break;
	}
}


function fcSendMngMsg(){

	  var sSendUser = gdsUser.GetColumn(0,"USER_ID");
	  var sRecvUser = "";
	  var sTitle	= "소모품승인";
	  var sDesc		= "승인여부 : " +cboProgStatCd.Text +" \n ";
		  sDesc		+="승인내용 : " +taReply.Text;

		  sRecvUser =  Trim(edAppliId.Text);


		  fcd_SendMessage(sSendUser		// 송신자 사번 
				, sRecvUser					// 수신자 사번 
				, sTitle	    			// 제목
				, "RBS3030" 	// 수신자 번호
				,  sDesc					// 수신자명
				, "comSendMsg"
				);							// 내용


}


function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    switch ( obj.id ) {
		case "cboProgStatCdSearch" :
			fcSearch();
			break;

	}

}

function rdoGbn_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	dsSupplCd.Filter("CD_TERM1='"+rdoGbn.Value+"'");
	fcSearch();
}
