/*-------------------------------------------------------------------------------------------------+
>>  dataset  OnRowPosChanged event 처리 
+-------------------------------------------------------------------------------------------------*/
function fcDsOnRowPosChanged(obj,nOldRow,nRow)
{
	 switch ( obj.id ) {
		//장비 목록 
		case "dsReportMana" :	// 보고서제출
			if(nRow>-1){	
					dsReportMng.ClearData();
					dsUploadFile.ClearData();

					fcReportSubmSearch();
			}

			break;
		case "dsReportMng" :	// 보고서제출
			if(nRow>-1){
					dsUploadFile.ClearData();
					fcSubmFileSearch();
			}

			break;
	 }

}


