/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 보고서 조회 완료 후
	if ( sServiceName == "rbs4030-service:searchReportMana" ) {

	}


	 // 보고서 제출 조회  완료 후
	if ( sServiceName == "rbs4030-service:searchReportSubm" ) {


	}

	//  보고서 제출 첨부파일 조회  완료 후
	if ( sServiceName == "rbs4030-service:searchSubmFile" ) {


	}

}


/*-------------------------------------------------------------------------------------------------+
>>  보고서 제출 조회 
+-------------------------------------------------------------------------------------------------*/
function fcReportSubmSearch(){
	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	 //보고서 제출 조회 
	 sServiceName = "rbs4030-service:searchReportSubm";
	 sInDataSet   = "";
	 sOutDataSet  = "dsReportMng=dsReportMng ";

	 //조회조건 : 
	 fcd_AddParam("DEPT_CD", dsReportMana.GetColumn(dsReportMana.row,"DEPT_CD"));
	 fcd_AddParam("STND_YEAR", dsReportMana.GetColumn(dsReportMana.row,"STND_YEAR"));
	 fcd_AddParam("SEQ", dsReportMana.GetColumn(dsReportMana.row,"SEQ"));
	 fcd_AddParam("SUBM_YN", cboSubmYn.Value);
	 fcd_AddParam("MNG_NM", edSearchMngNm.Text);


	 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}


/*-------------------------------------------------------------------------------------------------+
>>  보고서 제출 첨부파일  조회 
+-------------------------------------------------------------------------------------------------*/
function fcSubmFileSearch(){
	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";


	//양식 첨부파일 조회 
	 sServiceName = "rbs4030-service:searchSubmFile";
	 sInDataSet   = "";
	 sOutDataSet  = "dsUploadFile=dsUploadFile ";

	 //조회조건 : 
	 fcd_AddParam("ATT_FILE_KEY", dsReportMng.GetColumn(dsReportMng.row, "ATT_FILE_KEY"  ));

	 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);


}

