/*-------------------------------------------------------------------------------------------------+
>>  팀별로 관리하는 종목만 조회되게 권한 관리
+-------------------------------------------------------------------------------------------------*/
function fcGamefilter(obj)
{
	if (IsExistVar("sApro", true)) {
		dsGameCdOrg.Copy(dsGameCd);			
		if (sApro != "T" && sMnAuthRmk != "") {
			cboGameCd.Value = sMnAuthRmk;
			//cboGameCd.enable = false;
			var filterStr = "CD='" + sMnAuthRmk + "'";
			dsGameCd.Filter(filterStr);
			dsGameCdOrg.Filter(filterStr);
			return false;
		}
    }
    return true;
}
/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	if (!fcSearchCheck(obj)) return;// 필수조건 체크 및 그리드 초기화

	var sServiceName = "rbs5010-service:searchRacerList";  // Grid 조회
	var sInDataSet   = "";
	var sOutDataSet  = "dsRacerList=dsRacerList";     

	fcd_AddParam("GAME_CD"      , cboGameCd.Value);        // 종목코드  	
	fcd_AddParam("RETIRE_GBN"   , cboRetireGbn.Value);     // 재직여부 

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

	imgEmpPhoto.ImageID = "icon_pop";
}

function fcSearchCntr(obj) {

	if (!fcSearchCheck(obj)) return;// 필수조건 체크 및 그리드 초기화

	var sServiceName = "rbs5010-service:searchRacerContract";  // Grid 조회
	var sInDataSet   = "";
	var sOutDataSet  = "dsRacerCntr=dsRacerCntr";     

	fcd_AddParam("GAME_CD"      , cboGameCd.Value);        // 종목코드  	
	fcd_AddParam("RETIRE_GBN"   , cboRetireGbn.Value);     // 재직여부 
	fcd_AddParam("RACER_NM"     , edRacerNmSch.Text);		//선수명

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

	imgEmpPhoto.ImageID = "";
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {
	if ( !fcSaveCheck() ) return;

	var sServiceName = "rbs5010-service:saveRacerInfo";
	var sInDataSet   = "dsRacerList=dsRacerList:u dsRacerCntr=dsRacerCntr:u";
	var sOutDataSet  = "dsRacerList=dsRacerList dsRacerCntr=dsRacerCntr";

	fcd_AddParam("GAME_CD"     , cboGameCd.Value);        // 종목코드  	
	fcd_AddParam("RETIRE_GBN"  , cboRetireGbn.Value);     // 재직여부 

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
	gnGrpRow = dsRacerList.row;
}

