/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
    var nRow = 0;
	if (obj.GetForm().id == "specBtnRow") {
		nRow = dsRacerList.AddRow(); // Record Add
	} else if (obj.GetForm().id == "divCntrRow") {
		if (fnc_IsNull(dsRacerList.GetColumn(dsRacerList.row, "RACER_ID"))) {
			fnc_message("선수 기본정보를 먼저 저장하세요", "N");
			return;
		}
		nRow = dsRacerCntr.AddRow(); // Record Add
		dsRacerCntr.SetColumn(nRow, "RACER_ID",  dsRacerList.GetColumn(dsRacerList.row, "RACER_ID"));
		dsRacerCntr.SetColumn(nRow, "CNTR_YEAR", Mid(today(),0,4));	
		dsRacerCntr.SetColumn(nRow, "CNTR_YEAR", Mid(today(),0,4));	

	}		
}


