>>  행삽입 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInsRow(obj) {
    // 여기서는 사용하지 않는다.	
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {

	if (obj.GetForm().id == "specBtnRow") {

		var dCnt = 0;
		for(var i = 0; i < dsRacerList.GetRowCount(); i++)
			if(dsRacerList.GetColumn(i, "chk") == 1) dCnt ++;

		if(fnc_Message(SNIS_COM_1010, "Y", dCnt) == 0) return false;	

		for(var i = dsRacerList.GetRowCount() -1; i >= 0; i--) {
			if(dsRacerList.GetColumn(i, "chk") == 1) {
				dsRacerList.DeleteRow(i);
			}
		}

	} else if (obj.GetForm().id == "divCntrRow") {
		for(var i = dsRacerCntr.GetRowCount() -1;i>=0; i--) {
			if(dsRacerCntr.GetColumn(i, "chk") == 1) {
				dsRacerCntr.DeleteRow(i);
			}
		}
	}
}


/***************************************************************************************************
*                          CALL BACK 처리 부분                                                     *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회 완료 후
	if ( sServiceName == "common-service:searchCode" ) {
		if (fcGamefilter() == true) {		
			fcComboAddAll(cboGameCd, dsGameCd);
			fcComboAddAll(cboRetireGbn, dsRetireGbn);
		}
		cboRetireGbn.Index = 0;
	}			


	if ( sServiceName == "rbs5010-service:searchRacerList" ) {
		fcSearchCntr(this);
	}			

	if ( sServiceName == "rbs5010-service:searchRacerContract" ) {
		if (dsRacerList.row >= 0) {
			var filterStr = "RACER_ID='" + dsRacerList.GetColumn(dsRacerList.row, "RACER_ID") + "'";
			dsRacerCntr.Filter(filterStr);
		}
	}			

	// 저장 완료 후
	if ( sServiceName == "rbs5010-service:saveRacerInfo" ) {
		//fcSearch();  // 다시 조회한다.
		dsRacerList.row = gnGrpRow;		
		fcOnRowPosChanged(obj,0,gnGrpRow);
		if (dsTempUser.RowCount() > 0) {
			fcSaveTempUser();
		}
	}			


}


