+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    fcInit(obj);  // 그리드 초기화

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	var bRet = true;
	// 변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsRacerList) &&  !fnc_DatasetChangeCheck(dsRacerCntr) ) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	oDataSet          = dsRacerList;
	sMandatoryColID   = "EMP_NO|GAME_CD|RACER_TYPE";
	sMandatoryColName = "사번|종목|선수구분";
	oGrid             = grdRacerList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		bRet = false;
	}

    if (bRet == true) {
		// 필수항목체크
		oDataSet          = dsRacerCntr;
		sMandatoryColID   = "CNTR_YEAR|CNTR_AMT";
		sMandatoryColName = "연도|계약금액";
		oGrid             = grdRacerCntr;
		if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
			bRet = false;
		}
	}

	return bRet;
}


