		dsRacerList.SetColumn(nRow, "HP_NO", G_ds_return.GetColumn(0, "HP_NO"));
		dsRacerList.SetColumn(nRow, "TEL_NO", G_ds_return.GetColumn(0, "TEL_NO"));
		dsRacerList.SetColumn(nRow, "RETIRE_GBN", G_ds_return.GetColumn(0, "RETIRE_GBN"));
		dsRacerList.SetColumn(nRow, "WORK_FRDT", G_ds_return.GetColumn(0, "FST_ENTR_DT"));	//입사일자
		dsRacerList.SetColumn(nRow, "WORK_TODT", G_ds_return.GetColumn(0, "RESIGN_DT"));	//퇴사일자

		imgEmpPhoto.ImageID = "http://140.101.1.11:8090/snis/files/photo/hum/id/"+ G_ds_return.GetColumn(0, "EMP_NO") + ".jpg";

		if (G_ds_return.GetColumn(0, "RETIRE_GBN") == "1") {
			var iRow = dsTempUser.AddRow();
			dsTempUser.SetColumn(iRow, "GBN", "002");
			dsTempUser.SetColumn(iRow, "USER_ID", G_ds_return.GetColumn(0, "EMP_NO"));
			dsTempUser.SetColumn(iRow, "PSWD", "1");
			dsTempUser.SetColumn(iRow, "USER_NM", G_ds_return.GetColumn(0, "EMP_NM"));
			dsTempUser.SetColumn(iRow, "EMAIL", G_ds_return.GetColumn(0, "EMAIL"));
			dsTempUser.SetColumn(iRow, "HP_NO", G_ds_return.GetColumn(0, "HP_NO"));
			dsTempUser.SetColumn(iRow, "FLOC", G_ds_return.GetColumn(0, "FLOC"));
			dsTempUser.SetColumn(iRow, "FGRADE", G_ds_return.GetColumn(0, "FGRADE"));			
			dsTempUser.SetColumn(iRow, "ASSO_CD", G_ds_return.GetColumn(0, "ASSO_CD"));
			dsTempUser.SetColumn(iRow, "ASSO_NM", G_ds_return.GetColumn(0, "ASSO_NM"));
			dsTempUser.SetColumn(iRow, "DEPT_CD", G_ds_return.GetColumn(0, "DEPT_CD"));
			dsTempUser.SetColumn(iRow, "DEPT_NM", G_ds_return.GetColumn(0, "DEPT_NM"));			
			dsTempUser.SetColumn(iRow, "RETIRE_GBN", "1");		//퇴사
