function fcSearch(obj) {

	if (!fcSearchCheck(obj)) return;// ÇÊ¼öÁ¶°Ç Ã¼Å© ¹× ±×¸®µå ÃÊ±âÈ­

	var sServiceName = "rbs6010-service:searchSuptrList";  // Grid Á¶È¸
	var sInDataSet   = "";
	var sOutDataSet  = "dsSuptrList=dsSuptrList";     

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

	imgPhoto.ImageID = "icon_pop";
}

function fcSearchSuprtList(obj) {

	if (!fcSearchCheck(obj)) return;// ÇÊ¼öÁ¶°Ç Ã¼Å© ¹× ±×¸®µå ÃÊ±âÈ­

	var sServiceName = "rbs6010-service:searchSuprtList";  // Grid Á¶È¸
	var sInDataSet   = "";
	var sOutDataSet  = "dsSuprtList=dsSuprtList dsConduct=dsConduct dsSuptrCndct=dsSuptrCndct";     

	fcd_AddParam("SUPTR_ID", dsSuptrList.GetColumn(dsSuptrList.row, "USER_ID"));	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}


