/*-------------------------------------------------------------------------------------------------+
>>  Çà»èÁ¦ ¹öÆ° Å¬¸¯ ãÁ Ã³¸®
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {

	if (obj.GetForm().id == "specBtnRow") {

		var dCnt = 0;
		for(var i = 0; i < dsSuptrList.GetRowCount(); i++)
			if(dsSuptrList.GetColumn(i, "chk") == 1) dCnt ++;

		if(fnc_Message(SNIS_COM_1010, "Y", dCnt) == 0) return false;	

		for(var i = dsSuptrList.GetRowCount() -1; i >= 0; i--) {
			if(dsSuptrList.GetColumn(i, "chk") == 1) {
				dsSuptrList.DeleteRow(i);
			}
		}

	} else if (obj.GetForm().id == "divCntrRow") {
		for(var i = dsSuprtList.GetRowCount() -1;i>=0; i--) {
			if(dsSuprtList.GetColumn(i, "chk") == 1) {
				dsSuprtList.DeleteRow(i);
			}
		}
	}
}


