/*-------------------------------------------------------------------------------------------------+
>>  Transaction ÈÄ Ã³¸® ÇØ¾ß ÇÒ ³»¿ë!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// °øÅëÄÚµå Á¶È¸ ¿Ï·á ÈÄ
	if ( sServiceName == "common-service:searchCode" ) {

	}			


	if ( sServiceName == "rbs6010-service:searchSuptrList" ) {

	}			

	if ( sServiceName == "rbs6010-service:searchSuptrContract" ) {
		if (dsSuptrList.row >= 0) {
			var filterStr = "Suptr_ID='" + dsSuptrList.GetColumn(dsSuptrList.row, "Suptr_ID") + "'";
			dsSuprtList.Filter(filterStr);
		}
	}			

	// ÀúÀå ¿Ï·á ÈÄ
	if ( sServiceName == "rbs6010-service:saveSuptrInfo" ) {
		//fcSearch();  // ´Ù½Ã Á¶È¸ÇÑ´Ù.
		dsSuptrList.row = gnGrpRow;		
		fcOnRowPosChanged(obj,0,gnGrpRow);
		if (dsTempUser.RowCount() > 0) {
			fcSaveTempUser();
		}
	}			


}


