function fcOnRowPosChanged(obj,nOldRow,nRow)
{
	if (obj.id == "dsSuptrList") {
		if (dsSuptrList.rowcount() == 0) return;
		if (dsSuptrList.GetColumn(nRow, "RETIRE_GBN") == "1") { //Åð»çÁöÀÎ °æ¿ì
			edSuptrNm.BKColor = "bisque";
		} else {
			edSuptrNm.BKColor = "default";
		}
		var filterStr = "Suptr_ID='" + dsSuptrList.GetColumn(dsSuptrList.row, "Suptr_ID") + "'";
		dsSuprtList.Filter(filterStr);

		imgPhoto.ImageID = "";
		imgPhoto.ImageID = PHOTO_URL + dsSuptrList.GetColumn(nRow, "USER_ID") + ".jpg";


		fcSearchSuprtList(obj);
	}

}

