>>  Åð»çÀÚµéÀ» ÀÓ½Ã»ç¿ëÀÚ·Î µî·ÏÇÑ´Ù. ³ªÁß¿¡ Á¶È¸ÇÒ¶§ joinÇÏ±â À§ÇØ
+-------------------------------------------------------------------------------------------------*/
function fcSaveTempUser(obj) {    


	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	sServiceName = "rsy3050-service:saveUser";
	sInDataSet   = "dsTempUser=dsTempUser:u dsEstmUser=dsEstmUser:u";

	sOutDataSet  = "dsTempUser=dsTempUser ";

	fcd_AddParam("JOB_GBN", "01");

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


