/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	if (!fcSearchCheck(obj)) return;// 필수조건 체크 및 그리드 초기화

	var sServiceName = "rbs6010-service:searchSuptrList";  // Grid 조회
	var sInDataSet   = "";
	var sOutDataSet  = "dsSuptrList=dsSuptrList";     

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

	imgPhoto.ImageID = "icon_pop";
}

function fcSearchSuprtList(obj) {

	if (!fcSearchCheck(obj)) return;// 필수조건 체크 및 그리드 초기화

	var sServiceName = "rbs6010-service:searchSuprtList";  // Grid 조회
	var sInDataSet   = "";
	var sOutDataSet  = "dsSuprtList=dsSuprtList dsConduct=dsConduct dsSuptrCndct=dsSuptrCndct";     

	fcd_AddParam("SUPTR_ID", dsSuptrList.GetColumn(dsSuptrList.row, "USER_ID"));	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {
	if ( !fcSaveCheck() ) return;

	var sServiceName = "rbs6010-service:saveSuptrInfo";
	var sInDataSet   = "dsSuptrList=dsSuptrList:u";
	var sOutDataSet  = "dsSuptrList=dsSuptrList";

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
	gnGrpRow = dsSuptrList.row;
}

