/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
    var nRow = 0;
	if (obj.GetForm().id == "specBtnRow") {
		nRow = dsSuptrList.AddRow(); // Record Add
	} else if (obj.GetForm().id == "divCntrRow") {
		if (fnc_IsNull(dsSuptrList.GetColumn(dsSuptrList.row, "Suptr_ID"))) {
			fnc_message("기동요원 기본정보를 먼저 저장하세요", "N");
			return;
		}
		nRow = dsSuprtList.AddRow(); // Record Add
		dsSuprtList.SetColumn(nRow, "Suptr_ID",  dsSuptrList.GetColumn(dsSuptrList.row, "Suptr_ID"));
		dsSuprtList.SetColumn(nRow, "CNTR_YEAR", Mid(today(),0,4));	
		dsSuprtList.SetColumn(nRow, "CNTR_YEAR", Mid(today(),0,4));	

	}		
}


