function fcInsRow(obj) {
    // 여기서는 사용하지 않는다.	
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {

	if (obj.GetForm().id == "specBtnRow") {

		var dCnt = 0;
		for(var i = 0; i < dsSuptrList.GetRowCount(); i++)
			if(dsSuptrList.GetColumn(i, "chk") == 1) dCnt ++;

		if(fnc_Message(SNIS_COM_1010, "Y", dCnt) == 0) return false;	

		for(var i = dsSuptrList.GetRowCount() -1; i >= 0; i--) {
			if(dsSuptrList.GetColumn(i, "chk") == 1) {
				dsSuptrList.DeleteRow(i);
			}
		}

	} else if (obj.GetForm().id == "divCntrRow") {
		for(var i = dsSuprtList.GetRowCount() -1;i>=0; i--) {
			if(dsSuprtList.GetColumn(i, "chk") == 1) {
				dsSuprtList.DeleteRow(i);
			}
		}
	}
}


/***************************************************************************************************
*                          CALL BACK 처리 부분                                                     *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회 완료 후
	if ( sServiceName == "common-service:searchCode" ) {

	}			


	if ( sServiceName == "rbs6010-service:searchSuptrList" ) {

	}			

	if ( sServiceName == "rbs6010-service:searchSuptrContract" ) {
		if (dsSuptrList.row >= 0) {
			var filterStr = "Suptr_ID='" + dsSuptrList.GetColumn(dsSuptrList.row, "Suptr_ID") + "'";
			dsSuprtList.Filter(filterStr);
		}
	}			

	// 저장 완료 후
	if ( sServiceName == "rbs6010-service:saveSuptrInfo" ) {
		//fcSearch();  // 다시 조회한다.
		dsSuptrList.row = gnGrpRow;		
		fcOnRowPosChanged(obj,0,gnGrpRow);
		if (dsTempUser.RowCount() > 0) {
			fcSaveTempUser();
		}
	}			


}


