/*-------------------------------------------------------------------------------------------------+
>>  Combo가 바뀌면 조회한다.  
>>                             년도,  회계구분, 차수구분
+-------------------------------------------------------------------------------------------------*/
function fcCombo_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcSearch();	
}

function fcOnRowPosChanged(obj,nOldRow,nRow)
{
	if (obj.id == "dsSuptrList") {
		if (dsSuptrList.rowcount() == 0) return;
		if (dsSuptrList.GetColumn(nRow, "RETIRE_GBN") == "1") { //퇴사지인 경우
			edSuptrNm.BKColor = "bisque";
		} else {
			edSuptrNm.BKColor = "default";
		}
		var filterStr = "Suptr_ID='" + dsSuptrList.GetColumn(dsSuptrList.row, "Suptr_ID") + "'";
		dsSuprtList.Filter(filterStr);

		imgPhoto.ImageID = "";
		imgPhoto.ImageID = PHOTO_URL + dsSuptrList.GetColumn(nRow, "USER_ID") + ".jpg";


		fcSearchSuprtList(obj);
	}

}

/*-------------------------------------------------------------------------------------------------+
>>  퇴사자들을 임시사용자로 등록한다. 나중에 조회할때 join하기 위해
+-------------------------------------------------------------------------------------------------*/
function fcSaveTempUser(obj) {    


	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	sServiceName = "rsy3050-service:saveUser";
	sInDataSet   = "dsTempUser=dsTempUser:u dsEstmUser=dsEstmUser:u";

	sOutDataSet  = "dsTempUser=dsTempUser ";

	fcd_AddParam("JOB_GBN", "01");

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


/*-------------------------------------------------------------------------------------------------+
>>  전체(%)를 추가해주는 함수
+-------------------------------------------------------------------------------------------------*/
function fcComboAddAll(objCombo, objDs) 
{
	var nRow = objDs.InsertRow(0);
	objDs.SetColumn(nRow, "CD", "%");
	objDs.SetColumn(nRow, "CD_NM", "전체");
	objCombo.value = "%";
}
