/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    //sBUTTONLIST   = 'FT'+sInpt+'FTFT';     //초기화,조회,저장,삭제,엑셀,인쇄,닫기
    sBUTTONLIST   = 'FTFFFFT'; 

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//var yyyymm;
	calStrDt.Value = AddDate(today(), -60);
	calEndDt.Value = AddDate(today(),  30);

    //divBtnConfirm.btnOk.Visible      = false;
    cboTeamCd.Value = gdsUser.GetColumn(0, "TEAM_CD"); 
	if (IsExistVar("sApro", true)) {
		if (sApro=="T") bAdmin = true;
	}

    //권한 체크
    SettingButton(obj);

    //공통코드 조회
	fcd_GetComboDs(dsInComCd);

	// 부서장 조회
	fcSearchDeptCaptain();	
}

