/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch() {

	if (!fcSearchCheck()) return;// 필수조건 체크 및 그리드 초기화

	var sServiceName = "rbs6020-service:searchSuprtList";  // Grid 조회
	var sInDataSet   = "";
	var sOutDataSet  = "dsSuprtList=dsSuprtList";     

	fcd_AddParam("STR_DT",  calStrDt.Value);
	fcd_AddParam("END_DT",  calEndDt.Value);
	fcd_AddParam("REQ_TEAM_CD", cboTeamCd.Value);	
	fcd_AddParam("STAT_CD", cboStatCd.Value);	
	fcd_AddParam("SUPTR_ID", cboSuptr.Value);	


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchDeptCaptain() {


	var sServiceName = "rbs6020-service:searchDeptCaptain";  // Grid 조회
	var sInDataSet   = "";
	var sOutDataSet  = "dsDeptCpt=dsDeptCpt";     

	fcd_AddParam("TEAM_CD",  substr(gdsUser.GetColumn(0, "TEAM_CD"),2));

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {
	if ( !fcSaveCheck() ) return;

	// 관리자 저장시 접수
	if (bAdmin == true && dsSuprtList.GetColumn(dsSuprtList.row, "STAT_CD") == "004") {
		dsSuprtList.SetColumn(dsSuprtList.row, "STAT_CD", "011");
	} 
	// 요청자 저장시 결과입력
	if (dsSuprtList.GetColumn(dsSuprtList.row, "REQ_ID") == gdsUser.GetColumn(0, "USER_ID") && 
	    dsSuprtList.GetColumn(dsSuprtList.row, "STAT_CD") == "013") {
		dsSuprtList.SetColumn(dsSuprtList.row, "STAT_CD", "021");
	}

	var sServiceName = "rbs6020-service:saveSuprtInfo";
	var sInDataSet   = "dsSuprtList=dsSuprtList:u";
	var sOutDataSet  = "dsDupList=dsDupList ";

	fcd_AddParam("STR_DT",  calStrDt.Value);
	fcd_AddParam("END_DT",  calEndDt.Value);
	fcd_AddParam("REQ_TEAM_CD", cboTeamCd.Value);	
	fcd_AddParam("STAT_CD", cboStatCd.Value);	
	fcd_AddParam("SUPTR_ID", cboSuptr.Value);	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

