/***************************************************************************************************
*                          CALL BACK 처리 부분                                                     *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) {
		return;
	}


	// 공통코드 조회 완료 후
	if ( sServiceName == "common-service:searchCode" ) {
		dsStatCdAll.copy(dsStatCd);
		if (dsTeamCd.rowcount() > 0) {
			dsTeamCd.InsertRow(0);
			dsTeamCd.SetColumn(0, "CD", "%");
			dsTeamCd.SetColumn(0, "CD_NM", "전체");
		}
		if (bAdmin == true) cboTeamCd.Index = 0;

		CommCdSetting();

	} else if ( sServiceName == "rbs6020-service:searchSuprtList" ) {		// 조회 완료후		
		if (dsSuprtList.rowcount() == 0) {
			btnVibible("");
		} else {
			var floc = gdsUser.GetColumn(0, "FLOC");
			if (floc == "팀장") {
				dsSuprtList.sort("SORT_ODR:D,STR_DT:D");
			}
			dsSuprtList.sort("SORT_ODR:D,STR_DT:D");
		}
	} else if ( sServiceName == "rbs6020-service:searchDeptCaptain" ) {			// 부서장 조회후
		if (dsDeptCpt.rowcount()>0) {
			if (dsDeptCpt.GetColumn(0, "CAPTAIN_ID") ==  gdsUser.GetColumn(0, "USER_ID")) // 부서장인 경우
				bIsCaptain = "Y";
		}
		fcSearch();
	} else if ( sServiceName == "rbs6020-service:saveSuprtInfo" ) {			// 저장 완료 후
		fcSearch();
	} else if ( sServiceName == "rbs6020-service:saveSuprtStat" ) {			// 승인요청 후
		// 001:등록, 002:결재요청, 003:관리자반려, 004부서승인
		// 011:관리자 접수, 012:승인요청, 013:최종승인, 021:결과입력
		switch (slastStatCd) {
			case "001002":	//승인요청 : 지점장에게 쪽지 전송
				break;
			case "002001":	//부서 반려 ; 등록자에게 쪽지 전송
				break;
			case "002004":	//부서승인 ; 관리자에게 쪽지 전송
				break;
			case "011012":	// 관리부서 승인요청 ; 관리부서 결재자에게 쪽지 전송
				break;
			case "012011":	// 관리부서 결재자 반려 : 관리자에게 쪽지 전송
				break;
			case "012013":	// 등록자에게 쪽지 전송
				break;
		}
		fcSendMessenger(); // 쪽지, 문자 전송
		fcSearch();
	} else if ( sServiceName == "rbs6020-service:searchPendingCnt" ) {		//
		if (dsPendingCnt.rowcount() > 0) {
			var strMesg = "미처리중인 작업이 존재합니다.\n미처리내역-->\n";
			for (var iCnt=0;iCnt<dsPendingCnt.rowcount();iCnt++) {
				strMesg += dsPendingCnt.GetColumn(iCnt, "REQ_RSN") + "(" + 
				           dsPendingCnt.GetColumn(iCnt, "STR_DT") + "~" + 
				           dsPendingCnt.GetColumn(iCnt, "END_DT") + "), " +
				           dsPendingCnt.GetColumn(iCnt, "STAT_NM") + "\n" ;
			}
			fnc_message(strMesg, "N");			
		}
	}

}


