/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	var bRet = true;
	// 변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsSuprtList)) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	return bRet;
}

