function fcEmpDialog(obj)
{
	if (dsSuprtList.row < 0) return;

	var args  = "";		
	var bRtn = Dialog("common::comEmpSearchAll.xml", args, 334, 443, "");
	if(bRtn) {
		var nRow = dsSuprtList.Row;
		dsSuprtList.SetColumn(nRow, "SUPTR_ID",    G_ds_return.GetColumn(0, "EMP_NO"));
		dsSuprtList.SetColumn(nRow, "SUPTR_NM",    G_ds_return.GetColumn(0, "EMP_NM"));
		dsSuprtList.SetColumn(nRow, "REQ_TEAM_CD", G_ds_return.GetColumn(0, "TERM_CD"));
		dsSuprtList.SetColumn(nRow, "REQ_TEAM_NM", G_ds_return.GetColumn(0, "TEAM_NM"));
	}
}


/*-------------------------------------------------------------------------------------------------+
>>  Combo가 바뀌면 조회한다.  
>>                             년도,  회계구분, 차수구분
