function fcCombo_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcSearch();	
}

function fcOnRowPosChanged(obj,nOldRow,nRow)
{
	if (obj.id == "dsSuprtList") {	
		if (dsSuprtList.rowcount() == 0) return;
		tab0.Enable = true;
		if (dsSuprtList.GetColumn(nRow, "REQ_TEAM_CD") <> gdsUser.GetColumn(0, "TEAM_CD") &&
		    bAdmin == false) {
			tab0.TabIndex = 0;
			tab0.Enable = false;
		} else {
			if (dsSuprtList.GetColumn(nRow, "STAT_CD") >= "010" &&
		    bAdmin == false) {
				tab0.tab1.Enable = false;
			}
		}

		SettingButton(obj);

	}
}

