}
function CommCdSetting()
{
	var nRow = dsSTatCdAll.AddRow();
	dsSTatCdAll.SetColumn(nRow, "CD", "00");
	dsSTatCdAll.SetColumn(nRow, "CD_NM", "지점 진행");
	nRow = dsSTatCdAll.AddRow();
	dsSTatCdAll.SetColumn(nRow, "CD", "01");
	dsSTatCdAll.SetColumn(nRow, "CD_NM", "관리부서 진행");

	nRow = dsSuptrList.AddRow();
	dsSuptrList.SetColumn(nRow, "USER_ID", "%");
	dsSuptrList.SetColumn(nRow, "USER_NM", " ");
}

function setSuprtfiltering(curDay)
{
	var filterstr = "";
	filterstr = "STR_DT<='"+ curDay + "' && END_DT>='"+curDay+"'";
	dsSuprtList.Filter(filterstr);
}

function fcBtnOnClick(obj)
{

	var curStat = dsSuprtList.GetColumn(dsSuprtList.row, "STAT_CD");
	if (fnc_isnull(curStat)) curStat = "000";
	switch (obj.id) {
		case "btnAddRow":
			var nRow = dsSuprtList.AddRow();
			dsSuprtList.SetColumn(nRow, "STR_DT", 	   grid_calendar.UserData);
			dsSuprtList.SetColumn(nRow, "END_DT", 	   AddDate(grid_calendar.UserData,10));
			dsSuprtList.SetColumn(nRow, "STAT_CD", 	   "001");
			dsSuprtList.SetColumn(nRow, "REQ_TEAM_CD", gdsUser.GetColumn(0, "TEAM_CD"));
			dsSuprtList.SetColumn(nRow, "REQ_ID",      gdsUser.GetColumn(0, "USER_ID"));
			dsSuprtList.SetColumn(nRow, "REQ_NM",      gdsUser.GetColumn(0, "USER_NM"));
			dsSuprtList.SetColumn(nRow, "REQ_AREA",    "지점경주장비");

			fcCheckPendingCnt();
			break;			
		case "btnSave":
			fcSave();
			break;			
		case "btnDel":
			if (!fnc_message("자료를 삭제하시겠습니까?", "Y")) return;
			dsSuprtList.DeleteRow(dsSuprtList.row);
			fcSave();
			break;			

		case "btnReqAprv":		// 지점 승인요청
			fcSaveStat(curStat, "002");
			break;			
		case "btnWithdraw":		// 지점 결재요청 회수
			fcSaveStat(curStat, "001");
			break;			
		case "btnCnclReq":		// 취소요청	;; 별도 필드로 관리
			fcSaveStat(curStat, "100");
			break;			

		case "btnBrncAprv":		// 지점장 결재
			fcSaveStat(curStat, "004");
			break;			
		case "btnBrncReturn":	// 지점장 반려
			fcSaveStat(curStat, "003");
			break;			
		case "btnMngrReq":		// 관리자 결재 요청
			fcSaveStat(curStat, "013");
			break;			
		case "btnMngrReturn":	// 관리부서장 반려
			fcSaveStat(curStat, "011");
			break;			
		case "btnCancel":		//취소처리
			fcSaveStat(curStat, "200");
			break;
		case "btnContSave":
			fcContSave();
			break;
		case "btnPrint":
			fcPrint();
			break;
	}
}

// 권한에 따른 버튼 제어
function SettingButton(obj)
{
	var bEditable = false;

	// 상태,요청자,기동요원 수정가능 여부
	tab0.tab1.cboStatCd.Enable = bAdmin;
	//tab0.tab1.cboSuptr.Visible = bAdmin;
//	tab0.tab1.btnReqId.Enable = bAdmin;
	tab0.tab1.cboSuptrId.Enable = bAdmin;

	var btnStat = "";	
	if (dsSuprtList.row >= 0) {

		// 상태에 따른 버튼 제어
		var statCd = dsSuprtList.GetColumn(dsSuprtList.row, "STAT_CD");
		if (dsSuprtList.GetRowType(dsSuprtList.row) == "insert") statCd = "000";
		// 001:등록, 002:결재요청, 003:관리자반려, 004부서승인
		// 011:관리자 접수, 011:승인요청, 012:최종승인, 021:결과입력
		switch (statCd) {
			case "000":
				btnstat = "11100000000000";				
				break;
			case "001":
				if (dsSuprtList.GetColumn(dsSuprtList.row, "REQ_ID") == gdsUser.GetColumn(0, "USER_ID")) // 요청자인 경우
					btnstat = "11110000000000";				
				break;
			case "002":	// 지점 결재 요청
				if (dsSuprtList.GetColumn(dsSuprtList.row, "BRNC_APRV_ID") == gdsUser.GetColumn(0, "USER_ID")) // 결재자인 경우
					btnstat = "00000011000000";								
				else if (dsSuprtList.GetColumn(dsSuprtList.row, "REQ_ID") == gdsUser.GetColumn(0, "USER_ID")) // 요청자인 경우
					btnstat = "10001000000000";				
				break;
			case "003":// 반려
				if (bIsCaptain != "Y") 
					btnstat = "11110000000000";				
				break;
			case "004":	
				if (bAdmin==true)
					btnstat = "11000001000000";				
				else if (dsSuprtList.GetColumn(dsSuprtList.row, "BRNC_APRV_ID") == gdsUser.GetColumn(0, "USER_ID")) // 결재자인 경우
					btnstat = "00001000000000";								
				else if (dsSuprtList.GetColumn(dsSuprtList.row, "REQ_ID") == gdsUser.GetColumn(0, "USER_ID")) // 요청자인 경우
					btnstat = "10000100000000";				
				break;
			case "011":
				if (dsSuprtList.GetColumn(dsSuprtList.row, "REQ_ID") == gdsUser.GetColumn(0, "USER_ID")) // 요청자인 경우
					btnstat = "10000100000000";				
				else if (bAdmin==true)
					btnstat = "11000000100000";				
				break;
			case "013":
				if (dsSuprtList.GetColumn(dsSuprtList.row, "REQ_ID") == gdsUser.GetColumn(0, "USER_ID"))  // 요청자인 경우
					btnstat = "10000100000000";				
				else if (bAdmin) 					//승인완료시에도 관리자는 수정 가능 해야
					btnstat = "11000000000000";	
				break;
			case "021":	// 결과입력
				if (dsSuprtList.GetColumn(dsSuprtList.row, "REQ_ID") == gdsUser.GetColumn(0, "USER_ID"))  // 요청자인 경우
					btnstat = "11010000000000";				
				break;
			case "022":	// 결과승인요청
				if (dsSuprtList.GetColumn(dsSuprtList.row, "RSLT_APRV_ID") == gdsUser.GetColumn(0, "USER_ID"))  // 결재자인 경우
					btnstat = "10000011000000";				
				else if (dsSuprtList.GetColumn(dsSuprtList.row, "REQ_ID") == gdsUser.GetColumn(0, "USER_ID"))  // 요청자인 경우
					btnstat = "10001000000000";								
				break;
			case "031":	// 취소요청
				if (bAdmin)
					btnstat = "10000000000011";				
				break;

		}


		var CancelReqYn = dsSuprtList.GetColumn(dsSuprtList.row, "CNCL_STAT_CD");	// 취소요청상태
		if (statCd > "004" && CancelReqYn == "001") {	// 취소요청인경우 관리자는 취소버튼 활성화.. 권한문제 재검토 필요
			if (bAdmin==true)	
				if (length(btnstat) > 10) btnstat = left(btnstat,12) + "1";		
		}

		btnVibible(btnstat);		

		// 같은 부서이거나 관리자 인경우 수정 가능
		if (dsSuprtList.GetColumn(dsSuprtList.row, "REQ_TEAM_CD") == gdsUser.GetColumn(0, "TEAM_CD") ||
		    bAdmin==true) {
		    bEditable = true;
		}
		// 시작일자,종료일자, 지원분야, 지원사유 수정 가능 여부
		calStrDt.Enable  = bEditable;
		calEndDt.Enable  = bEditable;
		//edReqArea.Enable = bEditable;
		//edReqRsn.Enable  = bEditable;

		// 같은 부서인 경우 수정 가능. 직접 입력중인 경우
		if (dsSuprtList.GetColumn(dsSuprtList.row, "REQ_TEAM_CD") == gdsUser.GetColumn(0, "TEAM_CD") ||
		    statCd == "000") {
		    bEditable = true;
		} else {
			bEditable = false;
		}
		tab0.tab1.edReqArea.Enable = bEditable;
		tab0.tab1.edReqRsn.Enable  = bEditable;
		//tab0.tab1.calStrDt.Enable = bEditable;
		//tab0.tab1.calEndDt.Enable = bEditable;
		tab0.tab1.cboReqBrncCd.Enable = bEditable;

		btnContSave.Visible = iif(tab0.TabIndex == 1 && (statCd == "013" ||statCd=="021"), true, false);
		btnPrint.Visible = iif(tab0.TabIndex == 1 && statCd >= "021", true, false);

	} else {
		btnVibible(btnStat);
	}
}

function btnVibible(btnStat)
{
	var bdefault = "00000000000000";	
	var arrBtn = array(14);
	arrBtn[0]  = divBtn.btnAddRow;
	arrBtn[1]  = divBtn.btnSave;
	arrBtn[2]  = divBtn.btnDel;
	arrBtn[3]  = divBtn.btnReqAprv;
	arrBtn[4]  = divBtn.btnWithdraw;
	arrBtn[5]  = divBtn.btnCnclReq;	
	arrBtn[6]  = divBtn.btnBrncAprv;	
	arrBtn[7]  = divBtn.btnBrncReturn;	
	arrBtn[8]  = divBtn.btnMngrReq;	
	arrBtn[9]  = divBtn.btnMngrWithraw;
	arrBtn[10] = divBtn.btnMngrAprv;	
	arrBtn[11] = divBtn.btnMngrReturn;	
	arrBtn[12] = divBtn.btnCancel;
	arrBtn[13] = divBtn.btnCnclReturn;

	if (btnStat == "") btnStat = bdefault;	

	var leftval = 0;
	for(var i=0 ; i < arrBtn.length(); i++ ) {
		arrBtn[i].Visible = iif(substr(btnstat,i,1) == "1", true, false);
		if (i == 0) arrBtn[i].Visible = false; // 부서장인경우 추가 버튼 안보이게 함

		if (arrBtn[i].Visible) {
			arrBtn[i].left = leftval;
			leftval += arrBtn[i].width + 1;
		}
	}
	divBtn.left = 700 - leftval;
	divbtn.width = leftval;
}		

function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (obj.id == "cboTeamCd") {
		if (cboTeamCd.value <> gdsUser.GetColumn(0, "TEAM_CD") && bAdmin == false) divBtn.enable = false;
		else divBtn.enable = true;
	}
}

function fcTabOnChanged(obj,nOldIndex,nNewindex)
{
	if (obj.id == "Tab0") {
		btnContSave.Visible = iif(tab0.TabIndex == 1 && dsSuprtList.GetColumn(dsSuprtList.row, "STAT_CD") >= "013", true, false);
		btnPrint.Visible = iif(tab0.TabIndex == 1 && dsSuprtList.GetColumn(dsSuprtList.row, "STAT_CD") >= "013", true, false);
	}
}


function fcSendMessenger() {
	var sSendUserId   = gdsUser.GetColumn(0,"USER_ID");
	var sSendUserNm   = gdsUser.GetColumn(0,"USER_NM");
	var sSendUserHpNo = gdsUser.GetColumn(0,"HP_NO");
	var sMngr = "";

	for(var iRow=0;iRow<dsAlarmList.rowcount();iRow++) {
		if (dsAlarmList.GetColumn(iRow, "ALARM_GBN") == "001") {	//쪽지전송
			if (sMngr != "") sMngr += ",";
			sMngr = dsAlarmList.GetColumn(iRow, "RECV_ID");
		}
	}

	var sTitle	  = "경주지원기동반";
	var sDesc	  = "";
	var sLinkMenuId = "RBS6030";

	var nRow = dsSuprtList.findrow("SEQ_NO", slastSeqNo);
	var RecvID = "";

	// 001:등록, 002:결재요청, 003:관리자반려, 004부서승인		
	// 011:관리자 접수, 012:승인요청, 013:최종승인, 021:결과입력
	switch (slastStatCd) {
		case "001002":	//승인요청 : 지점장에게 쪽지 전송
		case "003002":	//승인요청 : 지점장에게 쪽지 전송
			sDesc = "[경주지원기동요원] 결재 요청 건이 있습니다.";
			RecvID = dsSuprtList.GetColumn(nRow, "BRNC_APRV_ID");
			break;
		case "002003":	//부서 반려 ; 등록자에게 쪽지 전송
			sDesc = "[경주지원기동요원] 결재 요청이 반려되었습니다.";
			RecvID = dsSuprtList.GetColumn(nRow, "REQ_ID");
			break;
		case "002004":	//부서승인 ; 관리자에게 쪽지 전송
			sDesc = "[경주지원기동요원] 지원요청 건이 있습니다.";
			RecvID = sMngr;
			break;
		case "011013":	// 등록자, 관리자에게 쪽지 전송
			sDesc = "[경주지원기동요원] 최종 승인이 완료되었습니다.";
			RecvID = dsSuprtList.GetColumn(nRow, "REQ_ID");
			break;
	}	
	slastStatCd = "";
	if (RecvID == "") return;

	fcd_SendMessage(gdsUser.GetColumn(0, "USER_ID")	// 송신자 사번 
					, RecvID	// 수신자 사번 
					, sTitle	    // 제목
					, sLinkMenuId 	// 호출할 메뉴ID
					, sDesc			// 내용
					, "comSendMsg"
					);
}

function fcCalCanChang(obj,strOldText,strText)
{
	if (obj.id == "calRsltReptrDt") {
		if (strText < dsSuprtList.GetColumn(dsSuprtList.row, "END_DT")) {
			fnc_message("작성일자는 지원종료일이후로 지정해야 합니다.", "N");
			return false;
		}
	}
	return true;
}

// 미처리중인 작업내역이 존재하는 지 체크
function fcCheckPendingCnt()
{
	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	sServiceName = "rbs6020-service:searchPendingCnt";
	sInDataSet   = "";
	sOutDataSet  = "dsPendingCnt=dsPendingCnt ";

	fcd_AddParam("REQ_TEAM_CD", gdsUser.GetColumn(0, "TEAM_CD"));

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}
